import React, { useEffect, useState } from 'react';
import './CharacterMascot.css';

const CharacterMascot = ({ animationState }) => {
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    setIsRunning(animationState.includes('run'));
  }, [animationState]);

  return (
    <div className={`character-container ${animationState}`}>
      {/* Character SVG - More attractive design */}
      <svg
        className={`character ${animationState}`}
        viewBox="0 0 120 160"
        width="120"
        height="160"
      >
        {/* Define gradients */}
        <defs>
          <linearGradient id="skinGradient" x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" style={{ stopColor: '#FFD4A3', stopOpacity: 1 }} />
            <stop offset="100%" style={{ stopColor: '#FFBF91', stopOpacity: 1 }} />
          </linearGradient>
          <linearGradient id="shirtGradient" x1="0%" y1="0%" x2="0%" y2="100%">
            <stop offset="0%" style={{ stopColor: '#FF6B9D', stopOpacity: 1 }} />
            <stop offset="100%" style={{ stopColor: '#E84B8A', stopOpacity: 1 }} />
          </linearGradient>
          <radialGradient id="gloss">
            <stop offset="0%" style={{ stopColor: 'white', stopOpacity: 0.4 }} />
            <stop offset="100%" style={{ stopColor: 'white', stopOpacity: 0 }} />
          </radialGradient>
        </defs>

        {/* Hair */}
        <ellipse cx="60" cy="22" rx="16" ry="15" fill="#4A4A4A" />
        {/* Hair shine */}
        <ellipse cx="55" cy="15" rx="8" ry="6" fill="url(#gloss)" />

        {/* Head */}
        <circle cx="60" cy="32" r="14" fill="url(#skinGradient)" stroke="#333" strokeWidth="0.5" />

        {/* Eyes - Expressive */}
        <ellipse cx="54" cy="30" rx="3.5" ry="4.5" fill="#333" />
        <ellipse cx="66" cy="30" rx="3.5" ry="4.5" fill="#333" />
        {/* Eye shine */}
        <circle cx="55.5" cy="28.5" r="1.5" fill="white" opacity="0.8" />
        <circle cx="67.5" cy="28.5" r="1.5" fill="white" opacity="0.8" />

        {/* Eyebrows - Confident */}
        <path d="M 50 26 Q 54 24.5 58 25.5" stroke="#333" strokeWidth="1" fill="none" strokeLinecap="round" />
        <path d="M 62 25.5 Q 66 24.5 70 26" stroke="#333" strokeWidth="1" fill="none" strokeLinecap="round" />

        {/* Nose */}
        <line x1="60" y1="30" x2="60" y2="36" stroke="#E8B8A0" strokeWidth="0.8" strokeLinecap="round" />

        {/* Mouth - Happy smile */}
        <path d="M 54 38 Q 60 41 66 38" stroke="#333" strokeWidth="1.5" fill="none" strokeLinecap="round" />

        {/* Neck */}
        <rect x="56" y="45" width="8" height="5" fill="url(#skinGradient)" />

        {/* Body/Shirt */}
        <path d="M 48 50 L 48 85 Q 48 90 52 92 L 68 92 Q 72 90 72 85 L 72 50 Z" fill="url(#shirtGradient)" stroke="#333" strokeWidth="0.8" />

        {/* Shirt shine */}
        <ellipse cx="58" cy="62" rx="8" ry="12" fill="url(#gloss)" />

        {/* Pocket detail */}
        <rect x="56" y="70" width="8" height="6" fill="rgba(0,0,0,0.1)" rx="1" />

        {/* Left Arm - Raised holding pen */}
        <g className="arm-left">
          <line x1="48" y1="55" x2="38" y2="40" stroke="#FFD4A3" strokeWidth="3.5" strokeLinecap="round" />
          <circle cx="38" cy="40" r="3.5" fill="url(#skinGradient)" stroke="#333" strokeWidth="0.8" />
        </g>

        {/* Right Arm - Down */}
        <g className="arm-right">
          <line x1="72" y1="55" x2="82" y2="75" stroke="#FFD4A3" strokeWidth="3.5" strokeLinecap="round" />
          <circle cx="82" cy="75" r="3.5" fill="url(#skinGradient)" stroke="#333" strokeWidth="0.8" />
        </g>

        {/* PEN/MARKER - Main element */}
        <g className="pen-item">
          {/* Pen body */}
          <line x1="38" y1="40" x2="18" y2="18" stroke="#FF6B6B" strokeWidth="3.5" strokeLinecap="round" />
          {/* Pen tip */}
          <circle cx="18" cy="18" r="4.5" fill="#FF3333" stroke="#333" strokeWidth="0.8" />
          {/* Pen cap */}
          <rect x="34" y="42" width="6" height="4" fill="#FFD93D" stroke="#333" strokeWidth="0.8" rx="1" />
          {/* Pen stripe */}
          <line x1="38" y1="40" x2="28" y2="28" stroke="#FFD93D" strokeWidth="1.5" opacity="0.7" strokeLinecap="round" />
        </g>

        {/* Left Leg - Running motion */}
        <g className="leg-left">
          <line x1="54" y1="92" x2="52" y2="115" stroke="#333" strokeWidth="3.5" strokeLinecap="round" />
          <ellipse cx="52" cy="118" rx="4" ry="5" fill="#2C3E50" stroke="#333" strokeWidth="0.8" />
        </g>

        {/* Right Leg - Running motion */}
        <g className="leg-right">
          <line x1="66" y1="92" x2="68" y2="115" stroke="#333" strokeWidth="3.5" strokeLinecap="round" />
          <ellipse cx="68" cy="118" rx="4" ry="5" fill="#2C3E50" stroke="#333" strokeWidth="0.8" />
        </g>

        {/* Shoes */}
        <ellipse cx="52" cy="123" rx="4.5" ry="4" fill="#FF6B9D" stroke="#333" strokeWidth="0.8" />
        <ellipse cx="68" cy="123" rx="4.5" ry="4" fill="#FF6B9D" stroke="#333" strokeWidth="0.8" />

        {/* Motion speed lines */}
        <g className="speed-lines" opacity="0.6">
          <line x1="85" y1="50" x2="100" y2="48" stroke="#4ECDC4" strokeWidth="2" strokeLinecap="round" />
          <line x1="87" y1="65" x2="105" y2="65" stroke="#4ECDC4" strokeWidth="2" strokeLinecap="round" />
          <line x1="85" y1="80" x2="102" y2="82" stroke="#4ECDC4" strokeWidth="2" strokeLinecap="round" />
        </g>
      </svg>

      {/* Shadow under character */}
      <div className="character-shadow"></div>

      {/* Dust/Sparkle particles */}
      <div className="particle particle-1"></div>
      <div className="particle particle-2"></div>
      <div className="particle particle-3"></div>
      <div className="particle particle-4"></div>
    </div>
  );
};

export default CharacterMascot;
