import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FiSend, FiTrash2, FiSettings, FiUpload } from 'react-icons/fi';
import './App.css';

const API_URL = 'http://localhost:8000';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [uploadQuery, setUploadQuery] = useState('');
  const messagesEndRef = useRef(null);
  const imageInputRef = useRef(null);
  const documentInputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    // Load conversation history on mount
    loadHistory();
  }, []);

  const loadHistory = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/history`);
      if (response.data.messages) {
        setMessages(response.data.messages);
      }
    } catch (error) {
      console.error('Failed to load history:', error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    setLoading(true);
    const userMessage = input;
    setInput('');

    // Add user message to UI immediately
    setMessages(prev => [...prev, {
      user_message: userMessage,
      ai_response: '',
      timestamp: new Date().toISOString(),
      isLoading: true
    }]);

    try {
      const response = await axios.post(`${API_URL}/api/chat`, {
        text: userMessage,
        user_name: 'User'
      });

      setMessages(prev => {
        const updated = [...prev];
        updated[updated.length - 1] = {
          ...response.data,
          isLoading: false
        };
        return updated;
      });
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages(prev => {
        const updated = [...prev];
        updated[updated.length - 1] = {
          ...updated[updated.length - 1],
          ai_response: 'âŒ Lá»—i káº¿t ná»‘i. Vui lÃ²ng kiá»ƒm tra server.',
          isLoading: false
        };
        return updated;
      });
    }

    setLoading(false);
  };

  const clearHistory = async () => {
    if (window.confirm('Báº¡n cháº¯c cháº¯n muá»‘n xÃ³a lá»‹ch sá»­ cuá»™c trÃ² chuyá»‡n?')) {
      try {
        await axios.delete(`${API_URL}/api/history`);
        setMessages([]);
      } catch (error) {
        console.error('Failed to clear history:', error);
      }
    }
  };

  const uploadImage = async (file) => {
    if (!file) return;
    
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', uploadQuery);

    try {
      const response = await axios.post(`${API_URL}/api/upload/image`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      setMessages(prev => [...prev, {
        user_message: `[Image: ${file.name}]${uploadQuery ? ` - ${uploadQuery}` : ''}`,
        ai_response: response.data.ai_response,
        timestamp: response.data.timestamp
      }]);
      
      setUploadQuery('');
      if (imageInputRef.current) imageInputRef.current.value = '';
    } catch (error) {
      console.error('Image upload failed:', error);
      alert('Failed to upload image');
    } finally {
      setLoading(false);
    }
  };

  const uploadDocument = async (file) => {
    if (!file) return;
    
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', uploadQuery);

    try {
      const response = await axios.post(`${API_URL}/api/upload/document`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      setMessages(prev => [...prev, {
        user_message: `[Document: ${file.name}]${uploadQuery ? ` - ${uploadQuery}` : ''}`,
        ai_response: response.data.ai_response || response.data.message,
        timestamp: response.data.timestamp || new Date().toISOString()
      }]);
      
      setUploadQuery('');
      if (documentInputRef.current) documentInputRef.current.value = '';
    } catch (error) {
      console.error('Document upload failed:', error);
      alert('Failed to upload document');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl bg-white rounded-2xl shadow-2xl overflow-hidden flex flex-col h-screen max-h-screen md:max-h-[600px]">
        
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-600 to-purple-600 p-4 text-white flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold">ðŸ¤– AIClone</h1>
            <p className="text-sm opacity-90">AI NhÃ¢n Báº£n Cá»§a Báº¡n</p>
          </div>
          <button
            onClick={() => setShowSettings(!showSettings)}
            className="p-2 hover:bg-white hover:bg-opacity-20 rounded-lg transition"
          >
            <FiSettings size={24} />
          </button>
        </div>

        {/* Settings Panel */}
        {showSettings && (
          <div className="bg-gray-100 border-b p-4 flex gap-2">
            <button
              onClick={clearHistory}
              className="flex items-center gap-2 bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition"
            >
              <FiTrash2 /> XÃ³a lá»‹ch sá»­
            </button>
          </div>
        )}

        {/* Messages Container */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-50">
          {messages.length === 0 ? (
            <div className="flex items-center justify-center h-full text-center text-gray-500">
              <div>
                <p className="text-4xl mb-4">ðŸ‘‹</p>
                <p>ChÃ o báº¡n! HÃ£y báº¯t Ä‘áº§u cuá»™c trÃ² chuyá»‡n vá»›i tÃ´i.</p>
              </div>
            </div>
          ) : (
            messages.map((msg, idx) => (
              <div key={idx} className="space-y-2">
                {/* User Message */}
                <div className="flex justify-end">
                  <div className="bg-blue-500 text-white rounded-2xl rounded-tr-none px-4 py-2 max-w-xs">
                    {msg.user_message}
                  </div>
                </div>

                {/* AI Response */}
                <div className="flex justify-start">
                  <div className="bg-gray-200 text-gray-800 rounded-2xl rounded-tl-none px-4 py-2 max-w-xs">
                    {msg.isLoading ? (
                      <div className="flex gap-2">
                        <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" />
                        <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }} />
                        <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }} />
                      </div>
                    ) : (
                      msg.ai_response
                    )}
                  </div>
                </div>
              </div>
            ))
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Input Form */}
        <div className="bg-white border-t p-4">
          <form onSubmit={sendMessage} className="flex gap-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Nháº­p tin nháº¯n cá»§a báº¡n..."
              className="flex-1 border border-gray-300 rounded-2xl px-4 py-2 focus:outline-none focus:border-blue-500"
              disabled={loading}
            />
            <button
              type="submit"
              disabled={loading || !input.trim()}
              className="bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white rounded-full p-2 transition"
            >
              <FiSend size={24} />
            </button>
          </form>
          <p className="text-xs text-gray-500 mt-2 text-center">
            Backend pháº£i cháº¡y trÃªn http://localhost:8000
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
