import React, { useState } from 'react';
import axios from 'axios';
import './AuthStyles.css';

const ForgotPassword = ({ onBackToLogin }) => {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [step, setStep] = useState('email'); // email or sent

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!email.trim()) {
      setError('Vui l√≤ng nh·∫≠p email');
      return;
    }

    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      setError('Email kh√¥ng h·ª£p l·ªá');
      return;
    }

    setLoading(true);

    try {
      await axios.post('http://localhost:8000/api/auth/forgot-password', {
        email: email.trim()
      });

      setStep('sent');
      setSuccess('‚úÖ Link reset m·∫≠t kh·∫©u ƒë√£ ƒë∆∞·ª£c g·ª≠i!');

    } catch (err) {
      setError(err.response?.data?.detail || 'L·ªói g·ª≠i link reset');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-form-container">
        <div className="auth-card">
          {step === 'email' ? (
            <>
              <h1 className="auth-title">üîê Forgot Password</h1>
              <p className="auth-subtitle">Enter your email to reset your password</p>

              {error && <div className="error-box">‚ùå {error}</div>}

              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <label className="auth-label">üìß Email</label>
                  <div className="auth-input-wrapper">
                    <input
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="your@email.com"
                      className="auth-input"
                      disabled={loading}
                    />
                  </div>
                </div>

                <button
                  type="submit"
                  className={`auth-button ${loading ? 'loading' : ''}`}
                  disabled={loading}
                >
                  {loading ? '‚è≥ Sending...' : 'üì® Send Reset Link'}
                </button>
              </form>

              <div className="auth-divider">Or</div>

              <button
                className="auth-button-secondary"
                onClick={onBackToLogin}
                disabled={loading}
              >
                ‚Üê Back to Login
              </button>

              <div className="auth-footer">
                <p>üí° <strong>Tip:</strong> Check your spam folder if you don't see the email</p>
              </div>
            </>
          ) : (
            <>
              <div style={{ textAlign: 'center', animation: 'fadeInUp 0.8s ease-out' }}>
                <div style={{ fontSize: '4rem', marginBottom: '20px', animation: 'pulse 2s ease-in-out infinite' }}>‚úâÔ∏è</div>
                <h1 className="auth-title">Email Sent!</h1>
                <p className="auth-subtitle">Check your inbox for the reset link</p>
                <p style={{ fontSize: '0.9rem', color: 'rgba(255, 255, 255, 0.8)', marginTop: '15px' }}>
                  Link sent to: <strong>{email}</strong>
                </p>
                <div style={{ background: 'rgba(76, 175, 80, 0.15)', border: '1px solid rgba(76, 175, 80, 0.4)', borderRadius: '10px', padding: '15px', marginTop: '20px', marginBottom: '20px', color: '#81c784', fontSize: '0.9rem' }}>
                  ‚úÖ The reset link will expire in 24 hours
                </div>
              </div>

              <button
                className="auth-button-secondary"
                onClick={onBackToLogin}
              >
                ‚Üê Back to Login
              </button>

              <div className="auth-footer">
                <p>üìã Didn't receive the email? Check your spam folder</p>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default ForgotPassword;
