import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ResetPassword.css';
import FloatingCharacter from './FloatingCharacter';

const ResetPassword = ({ onResetSuccess }) => {
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [passwordStrength, setPasswordStrength] = useState(0);

  // Extract token from URL query parameter
  const getTokenFromUrl = () => {
    const params = new URLSearchParams(window.location.search);
    return params.get('token');
  };

  const token = getTokenFromUrl();

  useEffect(() => {
    if (!token) {
      setError('‚ùå Link kh√¥ng h·ª£p l·ªá ho·∫∑c ƒë√£ h·∫øt h·∫°n');
    }
  }, [token]);

  // Calculate password strength
  useEffect(() => {
    if (newPassword.length === 0) {
      setPasswordStrength(0);
    } else {
      let strength = 0;
      if (newPassword.length >= 6) strength += 25;
      if (newPassword.length >= 10) strength += 25;
      if (/[a-z]/.test(newPassword)) strength += 15;
      if (/[A-Z]/.test(newPassword)) strength += 15;
      if (/[0-9]/.test(newPassword)) strength += 10;
      if (/[!@#$%^&*]/.test(newPassword)) strength += 10;
      setPasswordStrength(Math.min(strength, 100));
    }
  }, [newPassword]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!newPassword) {
      setError('Vui l√≤ng nh·∫≠p m·∫≠t kh·∫©u m·ªõi');
      return;
    }

    if (newPassword.length < 6) {
      setError('M·∫≠t kh·∫©u ph·∫£i √≠t nh·∫•t 6 k√Ω t·ª±');
      return;
    }

    if (newPassword !== confirmPassword) {
      setError('M·∫≠t kh·∫©u kh√¥ng tr√πng kh·ªõp');
      return;
    }

    setLoading(true);

    try {
      await axios.post('http://localhost:8000/api/auth/reset-password', {
        token: token,
        new_password: newPassword
      });

      setSuccess('‚úÖ M·∫≠t kh·∫©u ƒë√£ ƒë∆∞·ª£c ƒë·∫∑t l·∫°i th√†nh c√¥ng!');

      setTimeout(() => {
        onResetSuccess();
      }, 2000);

    } catch (err) {
      setError(err.response?.data?.detail || 'L·ªói ƒë·∫∑t l·∫°i m·∫≠t kh·∫©u');
    } finally {
      setLoading(false);
    }
  };

  const getPasswordStrengthLabel = () => {
    if (passwordStrength < 25) return 'Y·∫øu';
    if (passwordStrength < 50) return 'V·ª´a';
    if (passwordStrength < 75) return 'Kh√°';
    return 'M·∫°nh';
  };

  const getPasswordStrengthColor = () => {
    if (passwordStrength < 25) return '#FF6B6B';
    if (passwordStrength < 50) return '#FFD93D';
    if (passwordStrength < 75) return '#6BCB77';
    return '#4ECDC4';
  };

  if (!token) {
    return (
      <div className="reset-password-container">
        <div className="reset-password-box">
          <div className="reset-password-header error-header">
            <h1>‚ùå Link Kh√¥ng H·ª£p L·ªá</h1>
          </div>
          <div className="reset-password-content">
            <p className="error-message">Link reset m·∫≠t kh·∫©u kh√¥ng h·ª£p l·ªá ho·∫∑c ƒë√£ h·∫øt h·∫°n (24 gi·ªù)</p>
            <button 
              onClick={() => window.location.href = '/login'}
              className="btn-back"
            >
              ‚Üê Quay l·∫°i ƒëƒÉng nh·∫≠p
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="reset-password-container">
      <div className="reset-password-box">
        <FloatingCharacter />
        {/* Header */}
        <div className="reset-password-header">
          <h1>üîë ƒê·∫∑t L·∫°i M·∫≠t Kh·∫©u</h1>
        </div>

        {/* Content */}
        <div className="reset-password-content">
          {success ? (
            <div className="reset-success">
              <div className="success-icon">‚úÖ</div>
              <h2>M·∫≠t kh·∫©u ƒë√£ ƒë∆∞·ª£c ƒë·∫∑t l·∫°i!</h2>
              <p>B·∫°n s·∫Ω ƒë∆∞·ª£c chuy·ªÉn h∆∞·ªõng ƒë·∫øn trang ƒëƒÉng nh·∫≠p trong v√†i gi√¢y</p>
              <button 
                onClick={onResetSuccess}
                className="btn-primary"
              >
                ‚Üí ƒêƒÉng nh·∫≠p ngay
              </button>
            </div>
          ) : (
            <>
              <p className="reset-subtitle">
                Nh·∫≠p m·∫≠t kh·∫©u m·ªõi c·ªßa b·∫°n
              </p>

              <form onSubmit={handleSubmit} className="reset-form">
                {/* Password Input */}
                <div className="form-group">
                  <label className="form-label">
                    <span className="label-icon">üîí</span>
                    M·∫≠t kh·∫©u m·ªõi
                  </label>
                  <div className="password-input-wrapper">
                    <input
                      type={showPassword ? "text" : "password"}
                      value={newPassword}
                      onChange={(e) => setNewPassword(e.target.value)}
                      placeholder="Nh·∫≠p m·∫≠t kh·∫©u m·ªõi (t·ªëi thi·ªÉu 6 k√Ω t·ª±)"
                      className="form-input"
                      disabled={loading}
                    />
                    <button
                      type="button"
                      className="password-toggle"
                      onClick={() => setShowPassword(!showPassword)}
                      disabled={loading}
                    >
                      {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                    </button>
                  </div>

                  {/* Password Strength Meter */}
                  {newPassword && (
                    <div className="password-strength">
                      <div className="strength-bar">
                        <div 
                          className="strength-fill"
                          style={{
                            width: `${passwordStrength}%`,
                            backgroundColor: getPasswordStrengthColor()
                          }}
                        ></div>
                      </div>
                      <span className="strength-label">
                        ƒê·ªô m·∫°nh: <strong style={{ color: getPasswordStrengthColor() }}>
                          {getPasswordStrengthLabel()}
                        </strong>
                      </span>
                    </div>
                  )}
                </div>

                {/* Confirm Password Input */}
                <div className="form-group">
                  <label className="form-label">
                    <span className="label-icon">üîí</span>
                    X√°c nh·∫≠n m·∫≠t kh·∫©u
                  </label>
                  <input
                    type={showPassword ? "text" : "password"}
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    placeholder="Nh·∫≠p l·∫°i m·∫≠t kh·∫©u"
                    className="form-input"
                    disabled={loading}
                  />
                  {confirmPassword && newPassword === confirmPassword && (
                    <p className="success-text">‚úì M·∫≠t kh·∫©u tr√πng kh·ªõp</p>
                  )}
                  {confirmPassword && newPassword !== confirmPassword && (
                    <p className="error-text">‚úó M·∫≠t kh·∫©u kh√¥ng tr√πng kh·ªõp</p>
                  )}
                </div>

                {error && <div className="alert alert-error">{error}</div>}

                <button
                  type="submit"
                  className="btn-primary"
                  disabled={loading || !newPassword || !confirmPassword}
                >
                  {loading ? '‚è≥ ƒêang c·∫≠p nh·∫≠t...' : '‚úÖ ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u'}
                </button>
              </form>

              <div className="password-requirements">
                <h3>üìã Y√™u c·∫ßu m·∫≠t kh·∫©u:</h3>
                <ul>
                  <li className={newPassword.length >= 6 ? 'met' : ''}>
                    {newPassword.length >= 6 ? '‚úì' : '‚óã'} √çt nh·∫•t 6 k√Ω t·ª±
                  </li>
                  <li className={/[A-Z]/.test(newPassword) ? 'met' : ''}>
                    {/[A-Z]/.test(newPassword) ? '‚úì' : '‚óã'} Ch·ª©a ch·ªØ hoa (A-Z)
                  </li>
                  <li className={/[a-z]/.test(newPassword) ? 'met' : ''}>
                    {/[a-z]/.test(newPassword) ? '‚úì' : '‚óã'} Ch·ª©a ch·ªØ th∆∞·ªùng (a-z)
                  </li>
                  <li className={/[0-9]/.test(newPassword) ? 'met' : ''}>
                    {/[0-9]/.test(newPassword) ? '‚úì' : '‚óã'} Ch·ª©a s·ªë (0-9)
                  </li>
                </ul>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default ResetPassword;
