import React, { useState, useEffect } from 'react';
import Home from './Home';
import Chat from './Chat';
import HumanClone from './HumanClone';
import Admin from './Admin';
import Login from './Login';
import './App.css';

function App() {
  const [mode, setMode] = useState('home'); // 'home', 'chat', 'clone', 'admin', 'login'
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);

  // Check if user is already logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');
    const savedRole = localStorage.getItem('role');
    
    if (token && savedUser && savedRole) {
      setIsLoggedIn(true);
      setUser(JSON.parse(savedUser));
      setRole(savedRole);
    }
  }, []);

  const handleLogin = ({ user, role, token }) => {
    setIsLoggedIn(true);
    setUser(user);
    setRole(role);
    setMode('home');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('role');
    setIsLoggedIn(false);
    setUser(null);
    setRole(null);
    setMode('login');
  };

  // If not logged in, show login page
  if (!isLoggedIn) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <>
      {mode === 'home' && <Home onSelectMode={setMode} user={user} role={role} onLogout={handleLogout} />}
      {mode === 'admin' && role === 'admin' && <Admin user={user} onLogout={handleLogout} />}
      {mode === 'admin' && role !== 'admin' && <div style={{color: 'white', textAlign: 'center', padding: '50px'}}>⛔ Admin access only</div>}
      {mode === 'chat' && (
        <div style={{ position: 'relative' }}>
          <button
            onClick={() => setMode('home')}
            style={{
              position: 'absolute',
              top: 20,
              right: 20,
              zIndex: 1000,
              background: 'linear-gradient(135deg, #ff9a56 0%, #ff7043 100%)',
              color: 'white',
              border: 'none',
              padding: '12px 24px',
              borderRadius: '10px',
              cursor: 'pointer',
              fontSize: '1rem',
              fontWeight: 'bold',
              boxShadow: '0 4px 15px rgba(255, 112, 67, 0.3)',
              transition: 'all 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'scale(1.05)';
              e.target.style.boxShadow = '0 8px 25px rgba(255, 112, 67, 0.5)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'scale(1)';
              e.target.style.boxShadow = '0 4px 15px rgba(255, 112, 67, 0.3)';
            }}
          >
            ← Quay lại
          </button>
          <Chat />
        </div>
      )}
      {mode === 'clone' && (
        <div style={{ position: 'relative' }}>
          <button
            onClick={() => setMode('home')}
            style={{
              position: 'absolute',
              top: 20,
              left: 20,
              zIndex: 1000,
              background: 'rgba(255,255,255,0.1)',
              color: 'white',
              border: '2px solid rgba(255,255,255,0.2)',
              padding: '10px 20px',
              borderRadius: '25px',
              cursor: 'pointer',
              fontSize: '14px',
              backdropFilter: 'blur(10px)',
              transition: 'all 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.target.style.background = 'rgba(255,255,255,0.2)';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'rgba(255,255,255,0.1)';
            }}
          >
            ← Trang chủ
          </button>
          <HumanClone />
        </div>
      )}
    </>
  );
}

export default App;
