import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FiSend, FiTrash2, FiSettings, FiUpload, FiChevronUp, FiFile, FiMapPin, FiPlay, FiMic, FiType, FiSmile, FiThumbsUp } from 'react-icons/fi';
import './App.css';

const API_URL = 'http://localhost:8000';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [uploadQuery, setUploadQuery] = useState('');
  const [userName, setUserName] = useState('You');
  const [showUploadMenu, setShowUploadMenu] = useState(false);
  const messagesEndRef = useRef(null);
  const imageInputRef = useRef(null);
  const documentInputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    loadHistory();
  }, []);

  const loadHistory = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/history`);
      if (response.data.messages) {
        setMessages(response.data.messages);
      }
    } catch (error) {
      console.error('Failed to load history:', error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    setLoading(true);
    const userMessage = input;
    setInput('');

    setMessages(prev => [...prev, {
      user_message: userMessage,
      ai_response: '',
      timestamp: new Date().toISOString(),
      isLoading: true
    }]);

    try {
      const response = await axios.post(`${API_URL}/api/chat`, {
        text: userMessage,
        user_name: 'User'
      });

      setMessages(prev => {
        const updated = [...prev];
        updated[updated.length - 1] = {
          ...response.data,
          isLoading: false
        };
        return updated;
      });
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages(prev => {
        const updated = [...prev];
        updated[updated.length - 1] = {
          ...updated[updated.length - 1],
          ai_response: '❌ Lỗi kết nối. Vui lòng kiểm tra server.',
          isLoading: false
        };
        return updated;
      });
    }

    setLoading(false);
  };

  const clearHistory = async () => {
    if (window.confirm('Bạn chắc chắn muốn xóa lịch sử cuộc trò chuyện?')) {
      try {
        await axios.delete(`${API_URL}/api/history`);
        setMessages([]);
      } catch (error) {
        console.error('Failed to clear history:', error);
      }
    }
  };

  const uploadImage = async (file) => {
    if (!file) return;
    
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', uploadQuery);

    try {
      const response = await axios.post(`${API_URL}/api/upload/image`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      setMessages(prev => [...prev, {
        user_message: `[Image: ${file.name}]${uploadQuery ? ` - ${uploadQuery}` : ''}`,
        ai_response: response.data.ai_response,
        timestamp: response.data.timestamp
      }]);
      
      setUploadQuery('');
      if (imageInputRef.current) imageInputRef.current.value = '';
    } catch (error) {
      console.error('Image upload failed:', error);
      alert('Failed to upload image');
    } finally {
      setLoading(false);
    }
  };

  const uploadDocument = async (file) => {
    if (!file) return;
    
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', uploadQuery);

    try {
      const response = await axios.post(`${API_URL}/api/upload/document`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      setMessages(prev => [...prev, {
        user_message: `[Document: ${file.name}]${uploadQuery ? ` - ${uploadQuery}` : ''}`,
        ai_response: response.data.ai_response || response.data.message,
        timestamp: response.data.timestamp || new Date().toISOString()
      }]);
      
      setUploadQuery('');
      if (documentInputRef.current) documentInputRef.current.value = '';
    } catch (error) {
      console.error('Document upload failed:', error);
      alert('Failed to upload document');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 flex items-center justify-center p-4">
      <div className="w-full max-w-4xl bg-gray-800 rounded-2xl shadow-2xl overflow-hidden flex h-screen max-h-screen md:max-h-[600px] border border-gray-700">
        
        {/* LEFT SIDEBAR MENU */}
        <div className="w-56 bg-gray-900 border-r border-gray-700 p-4 flex flex-col gap-3 overflow-y-auto">
          {/* Menu Items */}
          <div className="space-y-2">
            {/* Share File */}
            <button
              onClick={() => imageInputRef.current?.click()}
              className="w-full flex items-center justify-between p-3 hover:bg-gray-700 rounded-lg transition text-gray-300 hover:text-white group"
              title="Chia sẻ file"
            >
              <span className="flex items-center gap-3">
                <FiFile size={20} className="text-blue-400" />
                <span className="text-sm font-medium">Chia sẻ file</span>
              </span>
              <FiChevronUp size={16} className="opacity-0 group-hover:opacity-100 transition" />
            </button>

            {/* Location */}
            <button
              className="w-full flex items-center justify-between p-3 hover:bg-gray-700 rounded-lg transition text-gray-300 hover:text-white group"
              title="Vị trí"
            >
              <span className="flex items-center gap-3">
                <FiMapPin size={20} className="text-red-400" />
                <span className="text-sm font-medium">Vị trí</span>
              </span>
              <FiChevronUp size={16} className="opacity-0 group-hover:opacity-100 transition" />
            </button>

            {/* Play Game */}
            <button
              className="w-full flex items-center justify-between p-3 hover:bg-gray-700 rounded-lg transition text-gray-300 hover:text-white group"
              title="Chơi game"
            >
              <span className="flex items-center gap-3">
                <FiPlay size={20} className="text-purple-400" />
                <span className="text-sm font-medium">Chơi game</span>
              </span>
              <FiChevronUp size={16} className="opacity-0 group-hover:opacity-100 transition" />
            </button>

            {/* Suggestions */}
            <button
              className="w-full flex items-center justify-between p-3 hover:bg-gray-700 rounded-lg transition text-gray-300 hover:text-white group"
              title="Tưởng tượng"
            >
              <span className="flex items-center gap-3">
                <FiSmile size={20} className="text-yellow-400" />
                <span className="text-sm font-medium">Tưởng tượng</span>
              </span>
              <FiChevronUp size={16} className="opacity-0 group-hover:opacity-100 transition" />
            </button>

            {/* Meta AI */}
            <button
              className="w-full flex items-center justify-between p-3 hover:bg-gray-700 rounded-lg transition text-gray-300 hover:text-white group"
              title="Meta AI"
            >
              <span className="flex items-center gap-3">
                <div className="w-5 h-5 border-2 border-cyan-400 rounded-full"></div>
                <span className="text-sm font-medium">Meta AI</span>
              </span>
              <FiChevronUp size={16} className="opacity-0 group-hover:opacity-100 transition" />
            </button>
          </div>

          <div className="border-t border-gray-700 my-2"></div>

          {/* Bottom Actions */}
          <div className="space-y-2 mt-auto">
            <button
              onClick={() => setShowSettings(!showSettings)}
              className="w-full flex items-center justify-between p-3 hover:bg-gray-700 rounded-lg transition text-gray-300 hover:text-white group"
              title="Cài đặt"
            >
              <span className="flex items-center gap-3">
                <FiSettings size={20} />
                <span className="text-sm font-medium">Cài đặt</span>
              </span>
              <FiChevronUp size={16} className="opacity-0 group-hover:opacity-100 transition" />
            </button>

            <button
              onClick={clearHistory}
              className="w-full flex items-center justify-between p-3 hover:bg-red-900 hover:bg-opacity-30 rounded-lg transition text-gray-300 hover:text-red-400 group"
              title="Xóa lịch sử"
            >
              <span className="flex items-center gap-3">
                <FiTrash2 size={20} />
                <span className="text-sm font-medium">Xóa lịch sử</span>
              </span>
              <FiChevronUp size={16} className="opacity-0 group-hover:opacity-100 transition" />
            </button>
          </div>
        </div>

        {/* MAIN CHAT AREA */}
        <div className="flex-1 flex flex-col bg-gray-800">
          {/* Header */}
          <div className="bg-gray-900 border-b border-gray-700 p-4 text-white flex justify-between items-center">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full flex items-center justify-center text-xl font-bold">
                AI
              </div>
              <div>
                <h1 className="text-xl font-bold">AIClone</h1>
                <p className="text-xs text-gray-400">Online</p>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <button className="p-2 hover:bg-gray-700 rounded-lg transition" title="Video call">
                📹
              </button>
              <button className="p-2 hover:bg-gray-700 rounded-lg transition" title="Phone call">
                ☎️
              </button>
            </div>
          </div>

          {/* Messages Container */}
          <div className="flex-1 overflow-y-auto p-5 space-y-4 bg-gray-800">
            {messages.length === 0 ? (
              <div className="flex items-center justify-center h-full text-center">
                <div>
                  <p className="text-6xl mb-4">💬</p>
                  <p className="text-gray-300 text-lg font-medium">Xin chào {userName}!</p>
                  <p className="text-gray-500 text-sm mt-2">Hãy bắt đầu cuộc trò chuyện với AIClone</p>
                </div>
              </div>
            ) : (
              messages.map((msg, idx) => (
                <div key={idx} className="space-y-3">
                  {/* User Message */}
                  <div className="flex justify-end gap-2">
                    <div className="max-w-xs">
                      <div className="text-xs text-gray-400 text-right mb-1 px-2 font-medium">{userName}</div>
                      <div className="bg-blue-600 text-white rounded-2xl rounded-tr-none px-5 py-2.5 shadow-lg break-words">
                        <p className="text-sm">{msg.user_message}</p>
                      </div>
                    </div>
                    <div className="w-8 h-8 rounded-full bg-blue-500 flex items-center justify-center flex-shrink-0 text-xs font-bold">
                      {userName.charAt(0).toUpperCase()}
                    </div>
                  </div>

                  {/* AI Response */}
                  <div className="flex justify-start gap-2">
                    <div className="w-8 h-8 rounded-full bg-gradient-to-br from-blue-500 to-blue-600 flex items-center justify-center flex-shrink-0 text-white text-xs font-bold">
                      AI
                    </div>
                    <div className="max-w-xs">
                      <div className="text-xs text-gray-400 mb-1 px-2 font-medium">AIClone</div>
                      <div className="bg-gray-700 text-gray-100 rounded-2xl rounded-tl-none px-5 py-2.5 shadow-lg break-words">
                        {msg.isLoading ? (
                          <div className="flex gap-2 items-center">
                            <span className="text-xs">Đang suy nghĩ</span>
                            <div className="flex gap-1">
                              <div className="w-1.5 h-1.5 bg-gray-400 rounded-full animate-bounce" />
                              <div className="w-1.5 h-1.5 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }} />
                              <div className="w-1.5 h-1.5 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }} />
                            </div>
                          </div>
                        ) : (
                          <p className="text-sm">{msg.ai_response}</p>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              ))
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Settings Panel */}
          {showSettings && (
            <div className="bg-gray-700 border-t border-gray-600 p-4 flex gap-2 items-center">
              <input
                type="text"
                value={userName}
                onChange={(e) => setUserName(e.target.value || 'You')}
                placeholder="Tên của bạn..."
                className="flex-1 border border-gray-600 rounded-lg px-3 py-2 text-sm bg-gray-600 text-white placeholder-gray-400 focus:outline-none focus:border-blue-500"
                maxLength={20}
              />
            </div>
          )}

          {/* Input Form */}
          <div className="bg-gray-800 border-t border-gray-700 p-4">
            <form onSubmit={sendMessage} className="flex gap-2 items-center relative">
              {/* Bottom Action Buttons */}
              <div className="flex gap-1">
                <button
                  type="button"
                  onClick={() => imageInputRef.current?.click()}
                  disabled={loading}
                  className="bg-gray-700 hover:bg-gray-600 disabled:bg-gray-600 text-gray-300 hover:text-white rounded-full p-2 transition"
                  title="📸 Tải ảnh"
                >
                  <span className="text-lg">📸</span>
                </button>
                
                <button
                  type="button"
                  onClick={() => documentInputRef.current?.click()}
                  disabled={loading}
                  className="bg-gray-700 hover:bg-gray-600 disabled:bg-gray-600 text-gray-300 hover:text-white rounded-full p-2 transition"
                  title="📎 Tải file"
                >
                  <span className="text-lg">📎</span>
                </button>

                <button
                  type="button"
                  disabled={loading}
                  className="bg-gray-700 hover:bg-gray-600 disabled:bg-gray-600 text-gray-300 hover:text-white rounded-full p-2 transition"
                  title="🎤 Voice"
                >
                  <FiMic size={18} />
                </button>

                <button
                  type="button"
                  disabled={loading}
                  className="bg-gray-700 hover:bg-gray-600 disabled:bg-gray-600 text-gray-300 hover:text-white rounded-full p-2 transition"
                  title="Aa Text size"
                >
                  <FiType size={18} />
                </button>
              </div>

              <input
                ref={imageInputRef}
                type="file"
                accept="image/*"
                onChange={(e) => uploadImage(e.target.files?.[0])}
                style={{ display: 'none' }}
              />
              
              <input
                ref={documentInputRef}
                type="file"
                accept=".pdf,.docx,.txt"
                onChange={(e) => uploadDocument(e.target.files?.[0])}
                style={{ display: 'none' }}
              />

              {/* Message Input */}
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Nhập tin nhắn..."
                className="flex-1 border border-gray-600 rounded-full px-5 py-2.5 text-sm text-white bg-gray-700 placeholder-gray-500 focus:outline-none focus:border-blue-500 transition"
                disabled={loading}
              />
              
              {/* Right Actions */}
              <div className="flex gap-1">
                <button
                  type="button"
                  disabled={loading}
                  className="bg-gray-700 hover:bg-gray-600 disabled:bg-gray-600 text-gray-300 hover:text-white rounded-full p-2 transition"
                  title="😊 Emoji"
                >
                  <FiSmile size={18} />
                </button>

                <button
                  type="submit"
                  disabled={loading || !input.trim()}
                  className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-600 text-white rounded-full p-2.5 transition font-medium"
                  title="Gửi"
                >
                  <FiSend size={18} />
                </button>

                <button
                  type="button"
                  disabled={loading}
                  className="bg-gray-700 hover:bg-gray-600 disabled:bg-gray-600 text-gray-300 hover:text-white rounded-full p-2 transition"
                  title="👍 Like"
                >
                  <FiThumbsUp size={18} />
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Chat;
