import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Chat from './Chat';

// AI Voice Chat Component - Inline
const AIVoiceChat = () => {
  const [isActive, setIsActive] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [messages, setMessages] = useState([]);
  
  const recognitionRef = useRef(null);
  const synthRef = useRef(window.speechSynthesis);
  const isActiveRef = useRef(false);
  const isSpeakingRef = useRef(false);
  
  // Khá»Ÿi táº¡o Speech Recognition
  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = true;
      recognitionRef.current.interimResults = true;
      recognitionRef.current.lang = 'vi-VN';
      
      recognitionRef.current.onresult = (event) => {
        let finalTranscript = '';
        let interimTranscript = '';
        
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript;
          } else {
            interimTranscript += transcript;
          }
        }
        
        if (interimTranscript) {
          setTranscript(interimTranscript);
        }
        
        if (finalTranscript) {
          setTranscript(finalTranscript);
          handleUserSpeech(finalTranscript);
        }
      };
      
      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        if (event.error === 'no-speech' && isActiveRef.current && !isSpeakingRef.current) {
          restartListening();
        }
      };
      
      recognitionRef.current.onend = () => {
        if (isActiveRef.current && !isSpeakingRef.current) {
          restartListening();
        }
      };
    }
    
    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      synthRef.current.cancel();
    };
  }, []);
  
  const restartListening = () => {
    if (recognitionRef.current && isActiveRef.current && !isSpeakingRef.current) {
      try {
        recognitionRef.current.start();
        setIsListening(true);
      } catch (e) {
        // Already started
      }
    }
  };
  
  const toggleActive = () => {
    if (!isActive) {
      setIsActive(true);
      isActiveRef.current = true;
      setMessages([{ role: 'bot', content: 'Xin chÃ o! TÃ´i lÃ  AI-Bot. HÃ£y nÃ³i chuyá»‡n vá»›i tÃ´i!' }]);
      speak('Xin chÃ o! TÃ´i lÃ  AI Bot. HÃ£y nÃ³i chuyá»‡n vá»›i tÃ´i!');
    } else {
      setIsActive(false);
      isActiveRef.current = false;
      setIsListening(false);
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      synthRef.current.cancel();
    }
  };
  
  const handleUserSpeech = async (text) => {
    if (!text.trim() || isSpeakingRef.current) return;
    
    setIsListening(false);
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
    
    setMessages(prev => [...prev, { role: 'user', content: text }]);
    setTranscript('');
    
    try {
      const res = await axios.post('http://localhost:8000/api/ai/chat', {
        message: text
      });
      
      const botResponse = res.data.response || res.data.message || 'TÃ´i khÃ´ng hiá»ƒu, báº¡n cÃ³ thá»ƒ nÃ³i láº¡i khÃ´ng?';
      setMessages(prev => [...prev, { role: 'bot', content: botResponse }]);
      speak(botResponse);
      
    } catch (error) {
      console.error('Error:', error);
      const errorMsg = 'Xin lá»—i, cÃ³ lá»—i xáº£y ra. HÃ£y thá»­ láº¡i!';
      setMessages(prev => [...prev, { role: 'bot', content: errorMsg }]);
      speak(errorMsg);
    }
  };
  
  const speak = (text) => {
    if (!synthRef.current) return;
    
    synthRef.current.cancel();
    
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = 'vi-VN';
    utterance.rate = 0.85;  // Cháº­m hÆ¡n Ä‘á»ƒ rÃµ rÃ ng
    utterance.pitch = 1.2;  // Giá»ng cao hÆ¡n, rÃµ hÆ¡n
    utterance.volume = 1;   // Ã‚m lÆ°á»£ng tá»‘i Ä‘a (1 lÃ  max cá»§a Web Speech API)
    
    // TÃ¬m giá»ng nÃ³i tá»‘t nháº¥t
    const voices = synthRef.current.getVoices();
    
    // Æ¯u tiÃªn: Google Vietnamese > Microsoft Vietnamese > Any Vietnamese
    let bestVoice = voices.find(voice => 
      voice.name.includes('Google') && voice.lang.includes('vi')
    );
    
    if (!bestVoice) {
      bestVoice = voices.find(voice => 
        voice.name.includes('Microsoft') && voice.lang.includes('vi')
      );
    }
    
    if (!bestVoice) {
      bestVoice = voices.find(voice => voice.lang.includes('vi'));
    }
    
    // Náº¿u khÃ´ng cÃ³ tiáº¿ng Viá»‡t, dÃ¹ng giá»ng ná»¯ tiáº¿ng Anh
    if (!bestVoice) {
      bestVoice = voices.find(voice => 
        voice.name.includes('Female') || voice.name.includes('Zira') || voice.name.includes('Samantha')
      );
    }
    
    if (bestVoice) {
      utterance.voice = bestVoice;
      console.log('Using voice:', bestVoice.name);
    }
    
    utterance.onstart = () => {
      setIsSpeaking(true);
      isSpeakingRef.current = true;
    };
    
    utterance.onend = () => {
      setIsSpeaking(false);
      isSpeakingRef.current = false;
      if (isActiveRef.current) {
        setTimeout(() => {
          restartListening();
        }, 500);
      }
    };
    
    synthRef.current.speak(utterance);
  };
  
  return (
    <div style={voiceStyles.container}>
      {/* Robot Avatar */}
      <div style={voiceStyles.avatarSection}>
        <div style={{
          ...voiceStyles.robot,
          animation: isActive ? 'float 3s ease-in-out infinite' : 'none',
        }}>
          <div style={voiceStyles.robotHead}>
            <div style={voiceStyles.robotFace}>
              <div style={voiceStyles.robotEyes}>
                <div style={{
                  ...voiceStyles.robotEye,
                  background: isActive ? '#00ff88' : '#00d4ff',
                  boxShadow: isActive ? '0 0 20px #00ff88' : '0 0 10px #00d4ff',
                  animation: isListening ? 'pulse 0.5s ease-in-out infinite alternate' : 'blink 4s infinite',
                }}></div>
                <div style={{
                  ...voiceStyles.robotEye,
                  background: isActive ? '#00ff88' : '#00d4ff',
                  boxShadow: isActive ? '0 0 20px #00ff88' : '0 0 10px #00d4ff',
                  animation: isListening ? 'pulse 0.5s ease-in-out infinite alternate' : 'blink 4s infinite',
                }}></div>
              </div>
              <div style={{
                ...voiceStyles.robotMouth,
                animation: isSpeaking ? 'speak 0.15s ease-in-out infinite alternate' : 'none',
              }}></div>
            </div>
          </div>
          <div style={voiceStyles.robotBody}>
            <div style={voiceStyles.robotChest}></div>
          </div>
        </div>
        
        {/* Status */}
        <div style={voiceStyles.status}>
          {!isActive && <span style={{ color: '#888' }}>ğŸ’¤ ChÆ°a hoáº¡t Ä‘á»™ng</span>}
          {isActive && isListening && !isSpeaking && <span style={{ color: '#00ff88' }}>ğŸ¤ Äang láº¯ng nghe...</span>}
          {isActive && isSpeaking && <span style={{ color: '#00d4ff' }}>ğŸ”Š Äang nÃ³i...</span>}
          {isActive && !isListening && !isSpeaking && <span style={{ color: '#ffd700' }}>â³ Äang xá»­ lÃ½...</span>}
        </div>
      </div>
      
      {/* Messages */}
      <div style={voiceStyles.messagesContainer}>
        <h3 style={voiceStyles.messagesTitle}>ğŸ’¬ Lá»‹ch sá»­ há»™i thoáº¡i</h3>
        <div style={voiceStyles.messagesList}>
          {messages.map((msg, idx) => (
            <div key={idx} style={{
              ...voiceStyles.message,
              background: msg.role === 'user' ? 'rgba(0, 200, 255, 0.1)' : 'rgba(0, 255, 136, 0.1)',
              borderLeft: msg.role === 'user' ? '3px solid #00d4ff' : '3px solid #00ff88',
            }}>
              <span style={voiceStyles.messageIcon}>{msg.role === 'user' ? 'ğŸ‘¤' : 'ğŸ¤–'}</span>
              <span style={voiceStyles.messageText}>{msg.content}</span>
            </div>
          ))}
          
          {transcript && isListening && (
            <div style={{
              ...voiceStyles.message,
              background: 'rgba(0, 200, 255, 0.05)',
              borderLeft: '3px dashed #00d4ff',
              opacity: 0.6,
            }}>
              <span style={voiceStyles.messageIcon}>ğŸ‘¤</span>
              <span style={voiceStyles.messageText}>{transcript}...</span>
            </div>
          )}
        </div>
      </div>
      
      {/* Control */}
      <div style={voiceStyles.controlSection}>
        <button 
          onClick={toggleActive}
          style={{
            ...voiceStyles.toggleBtn,
            background: isActive 
              ? 'linear-gradient(90deg, #ff4757 0%, #ff6b81 100%)' 
              : 'linear-gradient(90deg, #00d4ff 0%, #00ff88 100%)',
          }}
        >
          {isActive ? 'â¹ï¸ Dá»«ng láº¡i' : 'ğŸ™ï¸ Báº¯t Ä‘áº§u nÃ³i chuyá»‡n'}
        </button>
        <p style={voiceStyles.hint}>
          {isActive 
            ? 'HÃ£y nÃ³i, AI-Bot Ä‘ang láº¯ng nghe báº¡n...' 
            : 'Nháº¥n nÃºt Ä‘á»ƒ báº¯t Ä‘áº§u trÃ² chuyá»‡n báº±ng giá»ng nÃ³i'}
        </p>
      </div>
      
      {/* CSS Keyframes */}
      <style>{`
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        @keyframes blink {
          0%, 95%, 100% { transform: scaleY(1); }
          97% { transform: scaleY(0.1); }
        }
        @keyframes pulse {
          0% { box-shadow: 0 0 10px #00ff88; }
          100% { box-shadow: 0 0 30px #00ff88, 0 0 50px #00ff88; }
        }
        @keyframes speak {
          0% { height: 5px; width: 30px; border-radius: 5px; }
          100% { height: 20px; width: 25px; border-radius: 50%; }
        }
      `}</style>
    </div>
  );
};

