import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Chat from './Chat';

const UserHome = ({ user, role, onLogout }) => {
  const [tab, setTab] = useState('clones');
  const [clones, setClones] = useState([]);
  const [selectedClone, setSelectedClone] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [loading, setLoading] = useState(false);

  const API_URL = 'http://localhost:8000';
  const token = localStorage.getItem('token');

  useEffect(() => {
    loadClones();
  }, []);

  const loadClones = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/clones/`);
      setClones(response.data);
    } catch (error) {
      console.error('Error loading clones:', error);
    }
  };

  const handleSendMessage = async () => {
    if (!chatInput.trim() || !selectedClone) return;

    const newMessage = { role: 'user', content: chatInput };
    setChatMessages([...chatMessages, newMessage]);
    setChatInput('');
    setLoading(true);

    try {
      const response = await axios.post(
        `${API_URL}/api/clones/${selectedClone.id}/memory`,
        { message: chatInput, conversation_type: 'chat' },
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      setChatMessages(prev => [...prev, { role: 'assistant', content: response.data.response }]);
    } catch (error) {
      console.error('Error:', error);
      setChatMessages(prev => [...prev, { role: 'assistant', content: 'Error' }]);
    } finally {
      setLoading(false);
    }
  };

  const selectClone = (clone) => {
    setSelectedClone(clone);
    setChatMessages([]);
    setTab('chat');
  };

  // N·∫øu user ch∆∞a load xong, hi·ªán loading
  if (!user) {
    return (
      <div style={{ minHeight: '100vh', background: '#0a0a1a', color: 'white', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div>
          <h1 style={styles.title}>üß¨ AIClone</h1>
          <p style={styles.subtitle}>Welcome, {user.username || 'User'}!</p>
        </div>
        <button onClick={onLogout} style={styles.logoutBtn}>üö™ Logout</button>
      </div>

      <div style={styles.tabs}>
        <button
          onClick={() => setTab('clones')}
          style={{ ...styles.tab, background: tab === 'clones' ? '#4ECDC4' : 'rgba(255,255,255,0.1)' }}
        >
          üìö All Clones
        </button>
        {selectedClone && (
          <button
            onClick={() => setTab('chat')}
            style={{ ...styles.tab, background: tab === 'chat' ? '#27AE60' : 'rgba(255,255,255,0.1)' }}
          >
            üí¨ Chat with {selectedClone.name}
          </button>
        )}
        <button
          onClick={() => setTab('gemini')}
          style={{ ...styles.tab, background: tab === 'gemini' ? '#FF9800' : 'rgba(255,255,255,0.1)' }}
        >
          ü§ñ Chat with Gemini
        </button>
        <button
          onClick={() => setTab('aibot')}
          style={{ ...styles.tab, background: tab === 'aibot' ? '#00d4ff' : 'rgba(255,255,255,0.1)' }}
        >
          üéôÔ∏è AI Voice Chat
        </button>
      </div>

      <div style={styles.content}>
        {tab === 'clones' && (
          <div style={styles.section}>
            <h2 style={styles.sectionTitle}>All Available Clones</h2>
            <div style={styles.cloneGrid}>
              {clones.length === 0 ? (
                <p>No clones available</p>
              ) : (
                clones.map(clone => (
                  <div key={clone.id} onClick={() => selectClone(clone)} style={styles.cloneCard}>
                    {clone.face_image && (
                      <img src={`data:image/png;base64,${clone.face_image}`} style={styles.cloneImage} alt={clone.name} />
                    )}
                    <div style={styles.cloneInfo}>
                      <h3>{clone.name}</h3>
                      <p style={styles.cloneStyle}>{clone.speaking_style}</p>
                      <button style={styles.chatBtn}>Start Chat ‚Üí</button>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {tab === 'chat' && selectedClone && (
          <div style={styles.chatSection}>
            <h2 style={styles.sectionTitle}>Chatting with {selectedClone.name}</h2>
            <div style={styles.chatContainer}>
              <div style={styles.messagesBox}>
                {chatMessages.map((msg, idx) => (
                  <div key={idx} style={{ ...styles.message, alignSelf: msg.role === 'user' ? 'flex-end' : 'flex-start', background: msg.role === 'user' ? '#4ECDC4' : '#9B59B6' }}>
                    {msg.content}
                  </div>
                ))}
                {loading && <div style={styles.loading}>‚è≥ Thinking...</div>}
              </div>
              <div style={styles.inputBox}>
                <input
                  type="text"
                  value={chatInput}
                  onChange={(e) => setChatInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                  placeholder="Type your message..."
                  style={styles.chatInput}
                  disabled={loading}
                />
                <button onClick={handleSendMessage} disabled={loading} style={styles.sendBtn}>üì§ Send</button>
              </div>
            </div>
          </div>
        )}

        {tab === 'gemini' && (
          <div style={styles.section}>
            <Chat />
          </div>
        )}

        {tab === 'aibot' && (
          <div style={styles.section}>
            <AIVoiceChat />
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: { minHeight: '100vh', background: 'linear-gradient(135deg, #0a0a1a 0%, #1a1a3e 50%, #0a0a1a 100%)', color: 'white', fontFamily: "'Segoe UI', sans-serif" },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '30px 40px', background: 'rgba(0,0,0,0.3)', borderBottom: '1px solid rgba(255,255,255,0.1)' },
  title: { fontSize: '2rem', margin: '0', background: 'linear-gradient(90deg, #4ECDC4, #44A08D)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' },
  subtitle: { margin: '5px 0 0 0', color: 'rgba(255,255,255,0.7)', fontSize: '14px' },
  logoutBtn: { padding: '10px 20px', background: '#E74C3C', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' },
  tabs: { display: 'flex', gap: '10px', padding: '20px 40px', background: 'rgba(0,0,0,0.2)', borderBottom: '1px solid rgba(255,255,255,0.1)', overflowX: 'auto' },
  tab: { padding: '10px 20px', border: 'none', borderRadius: '8px', cursor: 'pointer', color: 'white', fontWeight: 'bold', fontSize: '14px', whiteSpace: 'nowrap', transition: 'all 0.3s' },
  content: { padding: '40px', maxWidth: '1400px', margin: '0 auto' },
  section: { marginBottom: '40px' },
