import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Register = ({ onRegisterSuccess, onBackToLogin }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showWebinarForm, setShowWebinarForm] = useState(false);

  // Load Visme form script
  useEffect(() => {
    if (showWebinarForm) {
      const script = document.createElement('script');
      script.src = 'https://static-bundles.visme.co/forms/static/embed.js';
      script.async = true;
      script.onload = () => {
        // Reload Visme forms after script loads
        if (window.Visme) {
          window.Visme.reload();
        }
      };
      document.body.appendChild(script);
      
      return () => {
        if (document.body.contains(script)) {
          document.body.removeChild(script);
        }
      };
    }
  }, [showWebinarForm]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    // Validation
    if (!formData.username.trim()) {
      setError('Username is required');
      return;
    }

    if (formData.username.length < 3) {
      setError('Username must be at least 3 characters');
      return;
    }

    if (!formData.email.trim()) {
      setError('Email is required');
      return;
    }

    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      setError('Invalid email format');
      return;
    }

    if (!formData.password) {
      setError('Password is required');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    setLoading(true);

    try {
      // Use new public registration endpoint
      const response = await axios.post('http://localhost:8000/api/auth/register', {
        username: formData.username,
        email: formData.email,
        password: formData.password
      });

      // Show success message with email confirmation info
      const emailConfirmMsg = response.data.email_sent 
        ? `‚úÖ Account created! Confirmation email sent to ${formData.email}`
        : `‚úÖ Account created! A confirmation email would be sent if email service is configured.`;
      
      setSuccess(emailConfirmMsg);
      
      // Redirect to login after 2 seconds
      setTimeout(() => {
        onRegisterSuccess();
      }, 2000);

    } catch (err) {
      const errorMessage = err.response?.data?.detail || 'Registration failed';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.box}>
        <h1 style={styles.title}>üß¨ Create Account</h1>
        <p style={styles.subtitle}>Join AIClone and chat with AI clones</p>

        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Username</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Choose a username"
              style={styles.input}
              disabled={loading}
            />
            <p style={styles.hint}>Min 3 characters, no spaces</p>
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="your@email.com"
              style={styles.input}
              disabled={loading}
            />
            <p style={styles.hint}>üìß Confirmation email will be sent to this address</p>
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Password</label>
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Min 6 characters"
              style={styles.input}
              disabled={loading}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Confirm Password</label>
            <input
              type={showPassword ? "text" : "password"}
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              placeholder="Confirm your password"
              style={styles.input}
              disabled={loading}
            />
          </div>

          <div style={styles.checkboxGroup}>
            <input
              type="checkbox"
              id="showPassword"
              checked={showPassword}
              onChange={(e) => setShowPassword(e.target.checked)}
              disabled={loading}
            />
            <label htmlFor="showPassword" style={styles.checkboxLabel}>
              Show Password
            </label>
          </div>

          {error && <div style={styles.error}>{error}</div>}
          {success && <div style={styles.success}>{success}</div>}

          <button
            type="submit"
            style={{
              ...styles.button,
              opacity: loading ? 0.6 : 1,
              cursor: loading ? 'not-allowed' : 'pointer'
            }}
            disabled={loading}
          >
            {loading ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>

        <div style={styles.divider}>
          <span>Already have an account?</span>
        </div>

        <button
          onClick={onBackToLogin}
          style={styles.loginLink}
          disabled={loading}
        >
          Back to Login
        </button>

        <div style={styles.info}>
          <p style={styles.infoTitle}>‚ú® What you get:</p>
          <ul style={styles.infoList}>
            <li>üí¨ Chat with AI clones</li>
            <li>üé® Create your own personal clones</li>
            <li>üë• Connect with other users</li>
            <li>üîê Secure account with password</li>
          </ul>
        </div>

        {/* Webinar Registration Section */}
        <div style={styles.webinarSection}>
          <button
            onClick={() => setShowWebinarForm(!showWebinarForm)}
            style={styles.webinarToggle}
          >
            {showWebinarForm ? '‚ñº' : '‚ñ∂'} Join Our Webinar (Optional)
          </button>
          
          {showWebinarForm && (
            <div style={styles.webinarContainer}>
              <div 
                className="visme_d"
