import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Chat from './Chat';

// Simple AI Voice Chat Component
const AIVoiceChat = () => {
  const [isActive, setIsActive] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [messages, setMessages] = useState([]);
  
  const recognitionRef = useRef(null);
  const synthRef = useRef(window.speechSynthesis);

  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = true;
      recognitionRef.current.interimResults = true;
      recognitionRef.current.lang = 'vi-VN';

      recognitionRef.current.onresult = (event) => {
        let finalTranscript = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
          if (event.results[i].isFinal) {
            finalTranscript += event.results[i][0].transcript;
          }
        }
        if (finalTranscript) {
          setTranscript(finalTranscript);
          handleUserSpeech(finalTranscript);
        }
      };

      recognitionRef.current.onend = () => {
        setIsListening(false);
        if (isActive && !isSpeaking) {
          try { recognitionRef.current.start(); } catch(e) {}
        }
      };
    }
    return () => {
      if (recognitionRef.current) recognitionRef.current.stop();
      synthRef.current.cancel();
    };
  }, [isActive, isSpeaking]);

  const handleUserSpeech = async (text) => {
    if (!text.trim()) return;
    setMessages(prev => [...prev, { role: 'user', content: text }]);
    setTranscript('');
    
    try {
      const res = await axios.post('http://localhost:8000/api/ai/chat', { message: text });
      const botResponse = res.data.response || 'Xin l·ªói, t√¥i kh√¥ng hi·ªÉu.';
      setMessages(prev => [...prev, { role: 'bot', content: botResponse }]);
      speak(botResponse);
    } catch (error) {
      speak('C√≥ l·ªói x·∫£y ra, th·ª≠ l·∫°i nh√©!');
    }
  };

  const speak = (text) => {
    synthRef.current.cancel();
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = 'vi-VN';
    utterance.rate = 1;
    
    // T√¨m gi·ªçng Google ti·∫øng Vi·ªát
    const voices = synthRef.current.getVoices();
    const viVoice = voices.find(v => v.name.includes('Google') && v.lang.startsWith('vi')) || 
                    voices.find(v => v.lang.startsWith('vi'));
    if (viVoice) utterance.voice = viVoice;
    
    utterance.onstart = () => setIsSpeaking(true);
    utterance.onend = () => {
      setIsSpeaking(false);
      if (isActive && recognitionRef.current) {
        setTimeout(() => {
          try { recognitionRef.current.start(); setIsListening(true); } catch(e) {}
        }, 500);
      }
    };
    synthRef.current.speak(utterance);
  };

  const toggleActive = async () => {
    if (!isActive) {
      // Request mic permission
      try {
        await navigator.mediaDevices.getUserMedia({ audio: true });
      } catch (err) {
        alert('Vui l√≤ng cho ph√©p microphone!');
        return;
      }
      setIsActive(true);
      setMessages([{ role: 'bot', content: 'Xin ch√†o! T√¥i l√† AI-Bot. H√£y n√≥i chuy·ªán v·ªõi t√¥i!' }]);
      speak('Xin ch√†o! T√¥i l√† AI Bot. H√£y n√≥i chuy·ªán v·ªõi t√¥i!');
    } else {
      setIsActive(false);
      setIsListening(false);
      if (recognitionRef.current) recognitionRef.current.stop();
      synthRef.current.cancel();
    }
  };

  return (
    <div style={voiceStyles.container}>
      <h2 style={voiceStyles.title}>ü§ñ AI Voice Assistant</h2>
      
      {/* Robot */}
      <div style={voiceStyles.robotSection}>
        <div style={{
          ...voiceStyles.robot,
          animation: isActive ? 'pulse 2s infinite' : 'none',
          background: isActive ? 'linear-gradient(135deg, #00d4ff, #00ff88)' : 'linear-gradient(135deg, #667eea, #764ba2)',
        }}>
          <div style={voiceStyles.robotFace}>
            <div style={{...voiceStyles.eye, background: isListening ? '#00ff88' : '#fff'}}></div>
            <div style={{...voiceStyles.eye, background: isListening ? '#00ff88' : '#fff'}}></div>
          </div>
          <div style={{...voiceStyles.mouth, background: isSpeaking ? '#00ff88' : '#fff'}}></div>
        </div>
        <p style={voiceStyles.status}>
          {isSpeaking ? 'üîä ƒêang n√≥i...' : isListening ? 'üé§ ƒêang nghe...' : isActive ? '‚è≥ Ch·ªù...' : 'üò¥ Nh·∫•n ƒë·ªÉ b·∫Øt ƒë·∫ßu'}
        </p>
      </div>

      {/* Messages */}
      <div style={voiceStyles.messages}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{
            ...voiceStyles.message,
            alignSelf: msg.role === 'user' ? 'flex-end' : 'flex-start',
            background: msg.role === 'user' ? '#4ECDC4' : '#667eea',
          }}>
            {msg.role === 'user' ? 'üßë' : 'ü§ñ'} {msg.content}
          </div>
        ))}
      </div>

      {/* Button */}
      <button onClick={toggleActive} style={{
        ...voiceStyles.btn,
        background: isActive ? '#ff4757' : 'linear-gradient(135deg, #00d4ff, #00ff88)',
      }}>
        {isActive ? '‚èπÔ∏è D·ª´ng' : 'üé§ B·∫Øt ƒë·∫ßu n√≥i chuy·ªán'}
      </button>

      <style>{`
        @keyframes pulse {
          0%, 100% { transform: scale(1); box-shadow: 0 0 20px rgba(0,212,255,0.5); }
          50% { transform: scale(1.05); box-shadow: 0 0 40px rgba(0,255,136,0.8); }
        }
      `}</style>
    </div>
  );
};

const voiceStyles = {
  container: { padding: '30px', background: 'rgba(0,0,0,0.3)', borderRadius: '20px', textAlign: 'center' },
  title: { color: '#00d4ff', marginBottom: '20px' },
  robotSection: { marginBottom: '20px' },
  robot: { width: '120px', height: '120px', borderRadius: '50%', margin: '0 auto 15px', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', cursor: 'pointer' },
  robotFace: { display: 'flex', gap: '20px', marginBottom: '10px' },
  eye: { width: '15px', height: '15px', borderRadius: '50%' },
  mouth: { width: '30px', height: '8px', borderRadius: '4px' },
  status: { color: 'rgba(255,255,255,0.7)', fontSize: '14px' },
  messages: { maxHeight: '300px', overflowY: 'auto', display: 'flex', flexDirection: 'column', gap: '10px', marginBottom: '20px', padding: '10px' },
  message: { padding: '10px 15px', borderRadius: '12px', color: 'white', maxWidth: '80%', fontSize: '14px' },
  btn: { padding: '15px 40px', border: 'none', borderRadius: '30px', color: 'white', fontSize: '16px', fontWeight: 'bold', cursor: 'pointer' },
};

const UserHome = ({ user, role, onLogout }) => {
  const [tab, setTab] = useState('clones');
  const [clones, setClones] = useState([]);
  const [selectedClone, setSelectedClone] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [loading, setLoading] = useState(false);

  const API_URL = 'http://localhost:8000';
  const token = localStorage.getItem('token');

  useEffect(() => {
    loadClones();
  }, []);

  const loadClones = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/clones/`);
      setClones(response.data);
    } catch (error) {
      console.error('Error loading clones:', error);
    }
  };

  const handleSendMessage = async () => {
    if (!chatInput.trim() || !selectedClone) return;

    const newMessage = { role: 'user', content: chatInput };
    setChatMessages([...chatMessages, newMessage]);
    setChatInput('');
    setLoading(true);

    try {
      const response = await axios.post(
        `${API_URL}/api/clones/${selectedClone.id}/memory`,
        { message: chatInput, conversation_type: 'chat' },
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      setChatMessages(prev => [...prev, { role: 'assistant', content: response.data.response }]);
    } catch (error) {
      console.error('Error:', error);
      setChatMessages(prev => [...prev, { role: 'assistant', content: 'Error' }]);
    } finally {
      setLoading(false);
    }
  };

  const selectClone = (clone) => {
    setSelectedClone(clone);
    setChatMessages([]);
    setTab('chat');
  };

  // N·∫øu user ch∆∞a load xong, hi·ªán loading
  if (!user) {
    return (
      <div style={{ minHeight: '100vh', background: '#0a0a1a', color: 'white', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div>
          <h1 style={styles.title}>üß¨ AIClone</h1>
          <p style={styles.subtitle}>Welcome, {user.username || 'User'}!</p>
        </div>
        <button onClick={onLogout} style={styles.logoutBtn}>üö™ Logout</button>
      </div>

      <div style={styles.tabs}>
        <button
          onClick={() => setTab('clones')}
          style={{ ...styles.tab, background: tab === 'clones' ? '#4ECDC4' : 'rgba(255,255,255,0.1)' }}
        >
          üìö All Clones
        </button>
        {selectedClone && (
          <button
            onClick={() => setTab('chat')}
            style={{ ...styles.tab, background: tab === 'chat' ? '#27AE60' : 'rgba(255,255,255,0.1)' }}
          >
            üí¨ Chat with {selectedClone.name}
          </button>
        )}
        <button
          onClick={() => setTab('gemini')}
          style={{ ...styles.tab, background: tab === 'gemini' ? '#FF9800' : 'rgba(255,255,255,0.1)' }}
        >
          ü§ñ Chat with Gemini
        </button>
        <button
          onClick={() => setTab('aibot')}
          style={{ ...styles.tab, background: tab === 'aibot' ? '#00d4ff' : 'rgba(255,255,255,0.1)' }}
        >
          üéôÔ∏è AI Voice Chat
        </button>
      </div>

      <div style={styles.content}>
        {tab === 'clones' && (
          <div style={styles.section}>
            <h2 style={styles.sectionTitle}>All Available Clones</h2>
            <div style={styles.cloneGrid}>
              {clones.length === 0 ? (
                <p>No clones available</p>
              ) : (
                clones.map(clone => (
                  <div key={clone.id} onClick={() => selectClone(clone)} style={styles.cloneCard}>
                    {clone.face_image && (
                      <img src={`data:image/png;base64,${clone.face_image}`} style={styles.cloneImage} alt={clone.name} />
                    )}
                    <div style={styles.cloneInfo}>
                      <h3>{clone.name}</h3>
                      <p style={styles.cloneStyle}>{clone.speaking_style}</p>
                      <button style={styles.chatBtn}>Start Chat ‚Üí</button>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {tab === 'chat' && selectedClone && (
          <div style={styles.chatSection}>
            <h2 style={styles.sectionTitle}>Chatting with {selectedClone.name}</h2>
            <div style={styles.chatContainer}>
              <div style={styles.messagesBox}>
                {chatMessages.map((msg, idx) => (
                  <div key={idx} style={{ ...styles.message, alignSelf: msg.role === 'user' ? 'flex-end' : 'flex-start', background: msg.role === 'user' ? '#4ECDC4' : '#9B59B6' }}>
                    {msg.content}
                  </div>
                ))}
                {loading && <div style={styles.loading}>‚è≥ Thinking...</div>}
              </div>
              <div style={styles.inputBox}>
                <input
                  type="text"
                  value={chatInput}
                  onChange={(e) => setChatInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                  placeholder="Type your message..."
                  style={styles.chatInput}
                  disabled={loading}
                />
                <button onClick={handleSendMessage} disabled={loading} style={styles.sendBtn}>üì§ Send</button>
              </div>
            </div>
          </div>
        )}

        {tab === 'gemini' && (
          <div style={styles.section}>
            <Chat />
          </div>
        )}

        {tab === 'aibot' && (
          <div style={styles.section}>
            <AIVoiceChat />
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: { minHeight: '100vh', background: 'linear-gradient(135deg, #0a0a1a 0%, #1a1a3e 50%, #0a0a1a 100%)', color: 'white', fontFamily: "'Segoe UI', sans-serif" },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '30px 40px', background: 'rgba(0,0,0,0.3)', borderBottom: '1px solid rgba(255,255,255,0.1)' },
  title: { fontSize: '2rem', margin: '0', background: 'linear-gradient(90deg, #4ECDC4, #44A08D)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' },
  subtitle: { margin: '5px 0 0 0', color: 'rgba(255,255,255,0.7)', fontSize: '14px' },
  logoutBtn: { padding: '10px 20px', background: '#E74C3C', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' },
  tabs: { display: 'flex', gap: '10px', padding: '20px 40px', background: 'rgba(0,0,0,0.2)', borderBottom: '1px solid rgba(255,255,255,0.1)', overflowX: 'auto' },
  tab: { padding: '10px 20px', border: 'none', borderRadius: '8px', cursor: 'pointer', color: 'white', fontWeight: 'bold', fontSize: '14px', whiteSpace: 'nowrap', transition: 'all 0.3s' },
  content: { padding: '40px', maxWidth: '1400px', margin: '0 auto' },
  section: { marginBottom: '40px' },
  sectionTitle: { fontSize: '1.8rem', marginBottom: '20px', color: 'white' },
  cloneGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' },
  cloneCard: { background: 'rgba(255,255,255,0.05)', border: '1px solid rgba(255,255,255,0.1)', borderRadius: '12px', overflow: 'hidden', cursor: 'pointer', transition: 'all 0.3s' },
  cloneImage: { width: '100%', height: '200px', objectFit: 'cover' },
  cloneInfo: { padding: '15px' },
  cloneStyle: { fontSize: '12px', color: 'rgba(255,255,255,0.6)', margin: '5px 0' },
  chatBtn: { width: '100%', padding: '8px', background: '#4ECDC4', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer', fontWeight: 'bold', marginTop: '10px' },
  chatSection: { marginBottom: '40px' },
  chatContainer: { display: 'flex', flexDirection: 'column', height: '600px', background: 'rgba(255,255,255,0.05)', borderRadius: '12px', overflow: 'hidden' },
  messagesBox: { flex: 1, padding: '20px', overflowY: 'auto', display: 'flex', flexDirection: 'column', gap: '10px' },
  message: { padding: '12px 16px', borderRadius: '8px', maxWidth: '60%', wordWrap: 'break-word' },
  loading: { color: 'rgba(255,255,255,0.6)', fontStyle: 'italic' },
  inputBox: { display: 'flex', gap: '10px', padding: '15px 20px', borderTop: '1px solid rgba(255,255,255,0.1)' },
  chatInput: { flex: 1, padding: '12px', background: 'rgba(255,255,255,0.1)', border: '1px solid rgba(255,255,255,0.2)', borderRadius: '8px', color: 'white', fontSize: '14px' },
  sendBtn: { padding: '12px 20px', background: '#4ECDC4', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' },
};

export default UserHome;
