import React, { useState, useEffect, useRef, useCallback } from 'react';
import * as THREE from 'three';
import axios from 'axios';

const HumanClone = () => {
  // Core states
  const [phase, setPhase] = useState('welcome'); // welcome, training, personality, clone
  const [cloneData, setCloneData] = useState({
    name: '',
    personality: [],
    speakingStyle: '',
    memories: [],
    faceData: null,
    voiceProfile: null,
  });

  // Training data
  const [trainingMessages, setTrainingMessages] = useState([]);
  const [personalityQuestions, setPersonalityQuestions] = useState([
    { q: 'Báº¡n mÃ´ táº£ tÃ­nh cÃ¡ch cá»§a mÃ¬nh nhÆ° tháº¿ nÃ o?', a: '', category: 'personality' },
    { q: 'Khi vui báº¡n thÆ°á»ng nÃ³i gÃ¬?', a: '', category: 'emotion' },
    { q: 'Khi buá»“n báº¡n thÆ°á»ng lÃ m gÃ¬?', a: '', category: 'emotion' },
    { q: 'CÃ¢u nÃ³i cá»­a miá»‡ng cá»§a báº¡n lÃ  gÃ¬?', a: '', category: 'speech' },
    { q: 'Báº¡n thÃ­ch nÃ³i chuyá»‡n kiá»ƒu nÃ o? (hÃ i hÆ°á»›c, nghiÃªm tÃºc, thÃ¢n thiá»‡n...)', a: '', category: 'style' },
    { q: 'Sá»Ÿ thÃ­ch cá»§a báº¡n lÃ  gÃ¬?', a: '', category: 'interest' },
    { q: 'Báº¡n ghÃ©t Ä‘iá»u gÃ¬ nháº¥t?', a: '', category: 'dislike' },
    { q: 'Má»¥c tiÃªu trong cuá»™c sá»‘ng cá»§a báº¡n?', a: '', category: 'goal' },
  ]);
  const [currentQuestion, setCurrentQuestion] = useState(0);

  // Clone interaction
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isThinking, setIsThinking] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [cloneEmotion, setCloneEmotion] = useState('neutral');
  const [cloneMood, setCloneMood] = useState(70); // 0-100 mood meter

  // 3D refs
  const mountRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const cloneRef = useRef(null);
  const rendererRef = useRef(null);
  
  // Input refs to maintain focus
  const nameInputRef = useRef(null);
  const answerInputRef = useRef(null);

  // Handle name change
  const handleNameChange = (e) => {
    setCloneData(prev => ({ ...prev, name: e.target.value }));
  };

  // Handle answer change in training
  const handleAnswer = (answer) => {
    const updated = [...personalityQuestions];
    updated[currentQuestion].a = answer;
    setPersonalityQuestions(updated);
  };

  const nextQuestion = () => {
    if (currentQuestion < personalityQuestions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      // Compile personality
      const personality = personalityQuestions.map(q => ({
        category: q.category,
        response: q.a,
      }));
      setCloneData(prev => ({ ...prev, personality }));
      setPhase('personality');
    }
  };

  // Speaking style states (moved to parent)
  const [sampleConvo, setSampleConvo] = useState('');
  const [catchphrases, setCatchphrases] = useState('');

  const finishSetup = async () => {
    try {
      await axios.post('http://localhost:8000/api/clone/setup', {
        name: cloneData.name,
        personality: cloneData.personality,
        speakingStyle: sampleConvo,
        catchphrases: catchphrases.split('\n').filter(p => p.trim()),
      });
    } catch (e) {
      console.log('Backend not available, using local mode');
    }

    setCloneData(prev => ({
      ...prev,
      speakingStyle: sampleConvo,
      memories: [],
    }));
    setPhase('clone');
  };

  // ============ RENDER WELCOME PHASE ============
  const renderWelcome = () => (
    <div style={styles.fullScreen}>
      <div style={styles.welcomeBox}>
        <div style={styles.glowOrb}>ðŸ§¬</div>
        <h1 style={styles.title}>Human Clone AI</h1>
        <p style={styles.subtitle}>
          Táº¡o nhÃ¢n báº£n AI cá»§a chÃ­nh báº¡n - biáº¿t suy nghÄ©, nÃ³i chuyá»‡n vÃ  hÃ nh Ä‘á»™ng nhÆ° báº¡n
        </p>

        <div style={styles.featureGrid}>
          <div style={styles.featureCard}>
            <span style={styles.featureIcon}>ðŸ§ </span>
            <h3>Há»c tÃ­nh cÃ¡ch</h3>
            <p>Clone há»c cÃ¡ch báº¡n suy nghÄ© vÃ  pháº£n á»©ng</p>
          </div>
          <div style={styles.featureCard}>
            <span style={styles.featureIcon}>ðŸ’¬</span>
            <h3>NÃ³i nhÆ° báº¡n</h3>
            <p>Clone nÃ³i chuyá»‡n vá»›i giá»ng Ä‘iá»‡u cá»§a báº¡n</p>
          </div>
          <div style={styles.featureCard}>
            <span style={styles.featureIcon}>ðŸ˜Š</span>
            <h3>Biá»ƒu cáº£m thá»±c</h3>
            <p>Cáº£m xÃºc vÃ  biá»ƒu cáº£m nhÆ° con ngÆ°á»i</p>
          </div>
          <div style={styles.featureCard}>
            <span style={styles.featureIcon}>ðŸŽ­</span>
            <h3>Nhá»› má»i thá»©</h3>
            <p>Clone ghi nhá»› vÃ  há»c tá»« cuá»™c trÃ² chuyá»‡n</p>
          </div>
        </div>

        <input
          ref={nameInputRef}
          type="text"
          placeholder="Nháº­p tÃªn cho Clone cá»§a báº¡n..."
          value={cloneData.name}
          onChange={handleNameChange}
          style={styles.nameInput}
          autoFocus
        />

        <button
          onClick={() => cloneData.name && setPhase('training')}
          disabled={!cloneData.name}
          style={{
            ...styles.primaryBtn,
            opacity: cloneData.name ? 1 : 0.5,
          }}
        >
          ðŸš€ Báº¯t Ä‘áº§u táº¡o {cloneData.name || 'Clone'}
        </button>
      </div>
    </div>
  );

  // ============ RENDER TRAINING PHASE ============
  const renderTraining = () => {
    const progress = ((currentQuestion + 1) / personalityQuestions.length) * 100;

    return (
      <div style={styles.fullScreen}>
        <div style={styles.trainingBox}>
          <div style={styles.progressBar}>
            <div style={{ ...styles.progressFill, width: `${progress}%` }} />
          </div>

          <h2 style={styles.phaseTitle}>
            ðŸ§  Dáº¡y {cloneData.name} hiá»ƒu báº¡n
          </h2>
          <p style={styles.questionCount}>
            CÃ¢u há»i {currentQuestion + 1} / {personalityQuestions.length}
          </p>

          <div style={styles.questionCard}>
            <p style={styles.question}>
              {personalityQuestions[currentQuestion].q}
            </p>
            <textarea
              ref={answerInputRef}
              key={`answer-${currentQuestion}`}
              value={personalityQuestions[currentQuestion].a}
              onChange={(e) => handleAnswer(e.target.value)}
              placeholder="Tráº£ lá»i cÃ ng chi tiáº¿t, Clone cÃ ng giá»‘ng báº¡n..."
              style={styles.answerInput}
              rows={4}
              autoFocus
            />
          </div>

          <div style={styles.btnGroup}>
            {currentQuestion > 0 && (
              <button
                onClick={() => setCurrentQuestion(currentQuestion - 1)}
                style={styles.secondaryBtn}
              >
                â† Quay láº¡i
              </button>
            )}
            <button
              onClick={nextQuestion}
              disabled={!personalityQuestions[currentQuestion].a}
              style={{
                ...styles.primaryBtn,
                opacity: personalityQuestions[currentQuestion].a ? 1 : 0.5,
              }}
            >
              {currentQuestion === personalityQuestions.length - 1 ? 'HoÃ n thÃ nh âœ“' : 'Tiáº¿p theo â†’'}
            </button>
          </div>
        </div>
      </div>
    );
  };

  // ============ RENDER PERSONALITY PHASE ============
  const renderPersonality = () => (
    <div style={styles.fullScreen}>
      <div style={styles.trainingBox}>
        <h2 style={styles.phaseTitle}>
          ðŸ’¬ Dáº¡y {cloneData.name} nÃ³i nhÆ° báº¡n
        </h2>

        <div style={styles.inputSection}>
          <label style={styles.label}>
            Viáº¿t má»™t Ä‘oáº¡n chat máº«u theo cÃ¡ch báº¡n thÆ°á»ng nÃ³i:
          </label>
          <textarea
            value={sampleConvo}
            onChange={(e) => setSampleConvo(e.target.value)}
            placeholder={`VÃ­ dá»¥:
- ÃŠ, cÃ³ gÃ¬ hot khÃ´ng?
- Oke luÃ´n, ez game!
- Hmm Ä‘á»ƒ tao nghÄ© xem...
- Chill Ä‘i bro, má»i chuyá»‡n sáº½ á»•n thÃ´i`}
            style={{ ...styles.answerInput, minHeight: '150px' }}
            autoFocus
          />
        </div>

        <div style={styles.inputSection}>
          <label style={styles.label}>
            Nhá»¯ng cÃ¢u cá»­a miá»‡ng báº¡n hay dÃ¹ng (má»—i cÃ¢u 1 dÃ²ng):
          </label>
          <textarea
            value={catchphrases}
            onChange={(e) => setCatchphrases(e.target.value)}
            placeholder={`VÃ­ dá»¥:
Chill Ä‘i
Oke luÃ´n
Ez game
Hmm...`}
            style={{ ...styles.answerInput, minHeight: '100px' }}
          />
        </div>

        <button onClick={finishSetup} style={styles.primaryBtn}>
          ðŸŽ­ Táº¡o {cloneData.name} ngay!
        </button>
      </div>
    </div>
  );

  // ============ PHASE 4: THE CLONE ============
  // Initialize 3D Clone
  useEffect(() => {
    if (phase !== 'clone' || !mountRef.current) return;

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x0a0a1a);

    const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.set(0, 0, 5);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.shadowMap.enabled = true;
    mountRef.current.appendChild(renderer.domElement);
    rendererRef.current = renderer;

    // Lighting
    const keyLight = new THREE.SpotLight(0xffffff, 1.5);
    keyLight.position.set(3, 5, 5);
    keyLight.castShadow = true;
    scene.add(keyLight);

    const fillLight = new THREE.DirectionalLight(0x6666ff, 0.3);
    fillLight.position.set(-3, 2, 3);
    scene.add(fillLight);

    const rimLight = new THREE.DirectionalLight(0xff6600, 0.4);
    rimLight.position.set(0, 3, -3);
    scene.add(rimLight);

    const ambient = new THREE.AmbientLight(0xffffff, 0.3);
    scene.add(ambient);

    // Create Human Clone Model
    const clone = new THREE.Group();
    scene.add(clone);
    cloneRef.current = clone;

    // Head with realistic proportions
    const headGeo = new THREE.SphereGeometry(1, 64, 64);
    const headMat = new THREE.MeshStandardMaterial({
      color: 0xf5c6a5,
      roughness: 0.4,
      metalness: 0.05,
    });
    const head = new THREE.Mesh(headGeo, headMat);
    head.scale.set(1, 1.2, 0.95);
    head.castShadow = true;
    clone.add(head);
    clone.userData.head = head;

    // Hair
    const hairGeo = new THREE.SphereGeometry(1.1, 64, 64, 0, Math.PI * 2, 0, Math.PI * 0.5);
    const hairMat = new THREE.MeshStandardMaterial({ color: 0x1a1a1a, roughness: 0.7 });
    const hair = new THREE.Mesh(hairGeo, hairMat);
    hair.position.y = 0.4;
    hair.scale.set(1.05, 1, 0.95);
    clone.add(hair);

    // Face features
    const createEye = (x) => {
      const eyeGroup = new THREE.Group();

      // White
      const whiteGeo = new THREE.SphereGeometry(0.2, 32, 32);
      const whiteMat = new THREE.MeshStandardMaterial({ color: 0xffffff });
      const white = new THREE.Mesh(whiteGeo, whiteMat);
      eyeGroup.add(white);

      // Iris
      const irisGeo = new THREE.CircleGeometry(0.12, 32);
      const irisMat = new THREE.MeshStandardMaterial({ color: 0x4a3728 });
      const iris = new THREE.Mesh(irisGeo, irisMat);
      iris.position.z = 0.18;
      eyeGroup.add(iris);

      // Pupil
      const pupilGeo = new THREE.CircleGeometry(0.05, 32);
      const pupilMat = new THREE.MeshBasicMaterial({ color: 0x000000 });
      const pupil = new THREE.Mesh(pupilGeo, pupilMat);
      pupil.position.z = 0.19;
      eyeGroup.add(pupil);

      // Highlight
      const hlGeo = new THREE.CircleGeometry(0.025, 16);
      const hlMat = new THREE.MeshBasicMaterial({ color: 0xffffff });
      const hl = new THREE.Mesh(hlGeo, hlMat);
      hl.position.set(0.04, 0.04, 0.2);
      eyeGroup.add(hl);

      eyeGroup.position.set(x, 0.35, 0.88);
      return eyeGroup;
    };

    const leftEye = createEye(-0.35);
    const rightEye = createEye(0.35);
    clone.add(leftEye);
    clone.add(rightEye);
    clone.userData.leftEye = leftEye;
    clone.userData.rightEye = rightEye;

    // Eyebrows
    const browGeo = new THREE.BoxGeometry(0.3, 0.06, 0.08);
    const browMat = new THREE.MeshStandardMaterial({ color: 0x1a1a1a });

    const leftBrow = new THREE.Mesh(browGeo, browMat);
    leftBrow.position.set(-0.35, 0.55, 0.85);
    leftBrow.rotation.z = 0.1;
    clone.add(leftBrow);
    clone.userData.leftBrow = leftBrow;

    const rightBrow = new THREE.Mesh(browGeo, browMat);
    rightBrow.position.set(0.35, 0.55, 0.85);
    rightBrow.rotation.z = -0.1;
    clone.add(rightBrow);
    clone.userData.rightBrow = rightBrow;

    // Nose
    const noseGeo = new THREE.ConeGeometry(0.1, 0.35, 16);
    const noseMat = new THREE.MeshStandardMaterial({ color: 0xf0b090 });
    const nose = new THREE.Mesh(noseGeo, noseMat);
    nose.position.set(0, 0.1, 0.95);
    nose.rotation.x = Math.PI * 0.1;
    clone.add(nose);

    // Mouth
    const mouthGroup = new THREE.Group();

    const upperLipGeo = new THREE.TorusGeometry(0.2, 0.04, 8, 32, Math.PI);
    const lipMat = new THREE.MeshStandardMaterial({ color: 0xc4756e });
    const upperLip = new THREE.Mesh(upperLipGeo, lipMat);
    upperLip.rotation.x = Math.PI;
    upperLip.position.y = 0.02;
    mouthGroup.add(upperLip);

    const lowerLipGeo = new THREE.TorusGeometry(0.18, 0.05, 8, 32, Math.PI);
    const lowerLip = new THREE.Mesh(lowerLipGeo, lipMat);
    lowerLip.position.y = -0.02;
    mouthGroup.add(lowerLip);

    // Mouth interior
    const interiorGeo = new THREE.PlaneGeometry(0.35, 0.2);
    const interiorMat = new THREE.MeshStandardMaterial({ color: 0x2a0a0a, side: THREE.DoubleSide });
    const interior = new THREE.Mesh(interiorGeo, interiorMat);
    interior.position.z = -0.03;
    interior.visible = false;
    mouthGroup.add(interior);
    clone.userData.mouthInterior = interior;

    mouthGroup.position.set(0, -0.4, 0.88);
    clone.add(mouthGroup);
    clone.userData.mouth = mouthGroup;

    // Ears
    const earGeo = new THREE.SphereGeometry(0.15, 16, 16);
    const earMat = new THREE.MeshStandardMaterial({ color: 0xf5c6a5 });

    const leftEar = new THREE.Mesh(earGeo, earMat);
    leftEar.position.set(-1, 0.2, 0);
    leftEar.scale.set(0.4, 1, 0.5);
    clone.add(leftEar);

    const rightEar = new THREE.Mesh(earGeo, earMat);
    rightEar.position.set(1, 0.2, 0);
    rightEar.scale.set(0.4, 1, 0.5);
    clone.add(rightEar);

    // Neck & Shoulders hint
    const neckGeo = new THREE.CylinderGeometry(0.4, 0.5, 0.8, 32);
    const neckMat = new THREE.MeshStandardMaterial({ color: 0xf5c6a5 });
    const neck = new THREE.Mesh(neckGeo, neckMat);
    neck.position.y = -1.6;
    clone.add(neck);

    // Animation state
    let breathe = 0;
    let blink = 0;
    let talk = 0;
    let idleMove = 0;

    // Mouse tracking
    const mouse = { x: 0, y: 0 };
    const onMouseMove = (e) => {
      mouse.x = (e.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(e.clientY / window.innerHeight) * 2 + 1;
    };
    window.addEventListener('mousemove', onMouseMove);

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);

      if (!cloneRef.current) return;
      const c = cloneRef.current;

      // Breathing
      breathe += 0.015;
      c.position.y = Math.sin(breathe) * 0.03;
      c.scale.x = 1 + Math.sin(breathe) * 0.008;

      // Idle subtle movement
      idleMove += 0.008;
      c.rotation.y += (mouse.x * 0.3 - c.rotation.y) * 0.05;
      c.rotation.x += (mouse.y * 0.15 - c.rotation.x) * 0.05;

      // Subtle head bob
      c.rotation.z = Math.sin(idleMove) * 0.02;

      // Eye tracking
      if (c.userData.leftEye && c.userData.rightEye) {
        c.userData.leftEye.rotation.y = mouse.x * 0.4;
        c.userData.leftEye.rotation.x = -mouse.y * 0.3;
        c.userData.rightEye.rotation.y = mouse.x * 0.4;
        c.userData.rightEye.rotation.x = -mouse.y * 0.3;
      }

      // Blinking
      blink++;
      if (blink > 200 && blink < 210) {
        const blinkAmt = 1 - Math.sin((blink - 200) / 10 * Math.PI);
        if (c.userData.leftEye) c.userData.leftEye.scale.y = Math.max(0.1, blinkAmt);
        if (c.userData.rightEye) c.userData.rightEye.scale.y = Math.max(0.1, blinkAmt);
      } else {
        if (c.userData.leftEye) c.userData.leftEye.scale.y = 1;
        if (c.userData.rightEye) c.userData.rightEye.scale.y = 1;
        if (blink > 210) blink = Math.random() * 100;
      }

      // Talking animation
      if (isSpeaking && c.userData.mouth) {
        talk += 0.4;
        const openAmt = Math.abs(Math.sin(talk)) * 0.4;
        c.userData.mouth.scale.y = 1 + openAmt;
        c.userData.mouthInterior.visible = openAmt > 0.15;
      } else {
        if (c.userData.mouth) {
          c.userData.mouth.scale.y = 1;
          c.userData.mouthInterior.visible = false;
        }
      }

      // Emotions
      if (c.userData.leftBrow && c.userData.rightBrow) {
        switch (cloneEmotion) {
          case 'happy':
            c.userData.leftBrow.position.y = 0.58;
            c.userData.rightBrow.position.y = 0.58;
            c.userData.leftBrow.rotation.z = -0.1;
            c.userData.rightBrow.rotation.z = 0.1;
            break;
          case 'sad':
            c.userData.leftBrow.rotation.z = -0.25;
            c.userData.rightBrow.rotation.z = 0.25;
            c.userData.leftBrow.position.y = 0.52;
            c.userData.rightBrow.position.y = 0.52;
            break;
          case 'thinking':
            c.userData.leftBrow.position.y = 0.58;
            c.userData.rightBrow.position.y = 0.53;
            c.userData.leftBrow.rotation.z = 0.2;
            c.userData.rightBrow.rotation.z = 0;
            break;
          case 'surprised':
            c.userData.leftBrow.position.y = 0.62;
            c.userData.rightBrow.position.y = 0.62;
            break;
          default:
            c.userData.leftBrow.position.y = 0.55;
            c.userData.rightBrow.position.y = 0.55;
            c.userData.leftBrow.rotation.z = 0.1;
            c.userData.rightBrow.rotation.z = -0.1;
        }
      }

      renderer.render(scene, camera);
    };

    animate();

    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('mousemove', onMouseMove);
      window.removeEventListener('resize', handleResize);
      if (mountRef.current && renderer.domElement) {
        mountRef.current.removeChild(renderer.domElement);
      }
    };
  }, [phase, isSpeaking, cloneEmotion]);

  // Generate clone response based on personality
  const generateCloneResponse = async (userMsg) => {
    setIsThinking(true);
    setCloneEmotion('thinking');

    // Build personality context
    const personalityContext = cloneData.personality.map(p =>
      `${p.category}: ${p.response}`
    ).join('\n');

    const catchphrases = cloneData.speakingStyle || '';

    // Create prompt that makes AI respond like the user
    const clonePrompt = `Báº¡n lÃ  ${cloneData.name}, má»™t nhÃ¢n báº£n AI cá»§a ngÆ°á»i dÃ¹ng. 
ÄÃ¢y lÃ  tÃ­nh cÃ¡ch vÃ  cÃ¡ch nÃ³i cá»§a báº¡n:
${personalityContext}

Phong cÃ¡ch nÃ³i chuyá»‡n máº«u:
${catchphrases}

QUAN TRá»ŒNG: HÃ£y tráº£ lá»i ÄÃšNG nhÆ° cÃ¡ch ngÆ°á»i dÃ¹ng sáº½ tráº£ lá»i. Sá»­ dá»¥ng cÃ¡c cÃ¢u cá»­a miá»‡ng, giá»ng Ä‘iá»‡u vÃ  tÃ­nh cÃ¡ch Ä‘Ã£ Ä‘Æ°á»£c training.
KhÃ´ng nÃ³i "TÃ´i lÃ  AI" hay "TÃ´i khÃ´ng thá»ƒ". HÃ£y tráº£ lá»i nhÆ° má»™t con ngÆ°á»i thá»±c sá»± vá»›i tÃ­nh cÃ¡ch Ä‘Ã£ cho.

Lá»‹ch sá»­ trÃ² chuyá»‡n:
${messages.slice(-6).map(m => `${m.role === 'user' ? 'NgÆ°á»i dÃ¹ng' : cloneData.name}: ${m.content}`).join('\n')}

NgÆ°á»i dÃ¹ng: ${userMsg}
${cloneData.name}:`;

    try {
      const response = await axios.post('http://localhost:8000/api/chat', {
        text: clonePrompt,
      });

      setIsThinking(false);
      return response.data.ai_response;
    } catch (error) {
      setIsThinking(false);

      // Fallback: generate response based on personality locally
      const styles = cloneData.speakingStyle.split('\n').filter(s => s.trim());
      const randomStyle = styles[Math.floor(Math.random() * styles.length)] || 'Oke';

      return `${randomStyle} ${userMsg.includes('?') ? 'Äá»ƒ tao nghÄ© xem...' : ''}`;
    }
  };

  // Detect emotion from text
  const detectEmotion = (text) => {
    const lower = text.toLowerCase();
    if (lower.match(/vui|haha|ðŸ˜Š|ðŸ˜„|tuyá»‡t|hay|thÃ­ch/)) return 'happy';
    if (lower.match(/buá»“n|ðŸ˜¢|ðŸ˜­|chÃ¡n|má»‡t/)) return 'sad';
    if (lower.match(/gÃ¬|sao|nhÆ° tháº¿ nÃ o|\?/)) return 'thinking';
    if (lower.match(/wow|á»“|ðŸ˜®|tháº­t sao/)) return 'surprised';
    return 'neutral';
  };

  // Send message
  const sendMessage = async () => {
    if (!inputText.trim() || isThinking) return;

    const userMsg = inputText.trim();
    setInputText('');
    setMessages(prev => [...prev, { role: 'user', content: userMsg }]);

    // Get clone response
    const cloneResponse = await generateCloneResponse(userMsg);

    // Detect and set emotion
    const emotion = detectEmotion(cloneResponse);
    setCloneEmotion(emotion);

    // Update mood based on conversation
    if (emotion === 'happy') setCloneMood(prev => Math.min(100, prev + 5));
    if (emotion === 'sad') setCloneMood(prev => Math.max(0, prev - 5));

    setMessages(prev => [...prev, { role: 'clone', content: cloneResponse }]);

    // Speak the response
    speakResponse(cloneResponse);
  };

  // Text-to-speech
  const speakResponse = (text) => {
    setIsSpeaking(true);

    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = 'vi-VN';
    utterance.rate = 1.0;
    utterance.pitch = 1.0;

    utterance.onend = () => {
      setIsSpeaking(false);
      setCloneEmotion('neutral');
    };

    window.speechSynthesis.speak(utterance);
  };

  // Voice input
  const startListening = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = 'vi-VN';
    recognition.continuous = false;

    recognition.onstart = () => setIsListening(true);
    recognition.onresult = (e) => {
      const text = e.results[0][0].transcript;
