          <span>{cloneEmotion === 'happy' ? 'ðŸ˜Š' : cloneEmotion === 'sad' ? 'ðŸ˜¢' : cloneEmotion === 'thinking' ? 'ðŸ¤”' : 'ðŸ˜'}</span>
        </div>
      </div>

      {/* Chat Panel */}
      <div style={styles.chatPanel}>
        <div style={styles.chatHeader}>
          ðŸ’¬ TrÃ² chuyá»‡n vá»›i {cloneData.name}
          {isThinking && <span style={styles.thinkingBadge}>ðŸ¤” Äang suy nghÄ©...</span>}
          {isSpeaking && <span style={styles.speakingBadge}>ðŸ”Š Äang nÃ³i...</span>}
        </div>

        <div style={styles.chatMessages}>
          {messages.length === 0 && (
            <div style={styles.emptyChat}>
              <p>ðŸ‘‹ ChÃ o! TÃ´i lÃ  {cloneData.name}, nhÃ¢n báº£n cá»§a báº¡n.</p>
              <p>HÃ£y nÃ³i chuyá»‡n vá»›i tÃ´i nhÆ° nÃ³i chuyá»‡n vá»›i chÃ­nh mÃ¬nh!</p>
            </div>
          )}
          {messages.map((msg, i) => (
            <div
              key={i}
              style={{
                ...styles.message,
                alignSelf: msg.role === 'user' ? 'flex-end' : 'flex-start',
                background: msg.role === 'user' ? '#ff7043' : 'rgba(255,255,255,0.1)',
              }}
            >
              {msg.content}
            </div>
          ))}
        </div>

        <div style={styles.chatInput}>
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
            placeholder={`NÃ³i gÃ¬ Ä‘Ã³ vá»›i ${cloneData.name}...`}
            style={styles.textInput}
          />
          <button
            onClick={startListening}
            disabled={isListening}
            style={{
              ...styles.iconBtn,
              background: isListening ? '#FF6B6B' : '#4ECDC4',
            }}
          >
            ðŸŽ¤
          </button>
          <button
            onClick={sendMessage}
            disabled={isThinking || !inputText.trim()}
            style={styles.iconBtn}
          >
            ðŸ’¬
          </button>
        </div>

        {/* Quick emotions */}
        <div style={styles.emotionBar}>
          <span style={{ opacity: 0.7 }}>Biá»ƒu cáº£m:</span>
          {['neutral', 'happy', 'sad', 'thinking', 'surprised'].map(em => (
            <button
              key={em}
              onClick={() => setCloneEmotion(em)}
              style={{
                ...styles.emotionBtn,
                border: cloneEmotion === em ? '2px solid #ff7043' : '2px solid transparent',
              }}
            >
              {em === 'neutral' && 'ðŸ˜'}
              {em === 'happy' && 'ðŸ˜Š'}
              {em === 'sad' && 'ðŸ˜¢'}
              {em === 'thinking' && 'ðŸ¤”'}
              {em === 'surprised' && 'ðŸ˜®'}
            </button>
          ))}
        </div>
      </div>

      {/* Reset button */}
      <button
        onClick={() => {
          setPhase('welcome');
          setMessages([]);
          setCloneData({ name: '', personality: [], speakingStyle: '', memories: [], faceData: null, voiceProfile: null });
          setCurrentQuestion(0);
          setPersonalityQuestions(prev => prev.map(q => ({ ...q, a: '' })));
        }}
        style={styles.resetBtn}
      >
        ðŸ”„ Táº¡o Clone má»›i
      </button>
    </div>
  );

  // Render based on phase
  return (
    <>
      {phase === 'welcome' && <WelcomeScreen />}
      {phase === 'training' && <TrainingScreen />}
      {phase === 'personality' && <PersonalityScreen />}
      {phase === 'clone' && <CloneScreen />}
    </>
  );
};

