    let skinColor = new THREE.Color(features.skinTone);

    // Check if skin is too dark and brighten it
    const skinHSL = {};
    skinColor.getHSL(skinHSL);
    if (skinHSL.l < 0.4) {
      // Too dark, use a nice default skin tone
      skinColor = new THREE.Color('#e8b89d');
    } else if (skinHSL.l < 0.55) {
      // Slightly boost brightness
      skinColor.setHSL(skinHSL.h, skinHSL.s * 0.8, skinHSL.l * 1.3);
