                filter: cloneEmotion === 'happy' ? 'brightness(1.1) saturate(1.1)' :
                        cloneEmotion === 'sad' ? 'brightness(0.9) saturate(0.8)' :
                        cloneEmotion === 'thinking' ? 'brightness(1.05)' :
                        'brightness(1)',
                animation: isSpeaking ? 'speaking 0.15s ease-in-out infinite' : 
                          isThinking ? 'thinking 2s ease-in-out infinite' : 
                          'idle 4s ease-in-out infinite',
              }}
            />
            
            {/* Speaking indicator - mouth animation overlay */}
            {isSpeaking && (
              <div style={styles.speakingWave}>
                <div style={styles.waveBar}></div>
                <div style={{...styles.waveBar, animationDelay: '0.1s'}}></div>
                <div style={{...styles.waveBar, animationDelay: '0.2s'}}></div>
                <div style={{...styles.waveBar, animationDelay: '0.3s'}}></div>
                <div style={{...styles.waveBar, animationDelay: '0.4s'}}></div>
              </div>
            )}
            
            {/* Emotion indicator */}
            <div style={styles.emotionBubble}>
              {cloneEmotion === 'happy' && 'ðŸ˜Š'}
              {cloneEmotion === 'sad' && 'ðŸ˜¢'}
              {cloneEmotion === 'thinking' && 'ðŸ¤”'}
              {cloneEmotion === 'surprised' && 'ðŸ˜®'}
              {cloneEmotion === 'neutral' && 'ðŸ˜Œ'}
            </div>
            
            {/* Name tag */}
