          <div style={styles.noFacePlaceholder}>
            <span style={{fontSize: '100px'}}>ðŸ‘¤</span>
            <p>KhÃ´ng cÃ³ áº£nh khuÃ´n máº·t</p>
          </div>
        )}
        
        {/* Mood indicator */}
        <div style={styles.moodIndicator}>
          <div style={styles.moodLabel}>TÃ¢m tráº¡ng</div>
