      </div>

      {/* Chat Panel */}
      <div style={styles.chatPanel}>
        <div style={styles.chatHeader}>
          ðŸ’¬ TrÃ² chuyá»‡n vá»›i {cloneData.name}
          {isThinking && <span style={styles.thinkingBadge}>ðŸ¤” Äang suy nghÄ©...</span>}
          {isSpeaking && <span style={styles.speakingBadge}>ðŸ”Š Äang nÃ³i...</span>}
        </div>

        <div style={styles.chatMessages}>
          {messages.length === 0 && (
            <div style={styles.emptyChat}>
              <p>ðŸ‘‹ ChÃ o! TÃ´i lÃ  {cloneData.name}, nhÃ¢n báº£n cá»§a báº¡n.</p>
              <p>HÃ£y nÃ³i chuyá»‡n vá»›i tÃ´i nhÆ° nÃ³i chuyá»‡n vá»›i chÃ­nh mÃ¬nh!</p>
            </div>
          )}
          {messages.map((msg, i) => (
            <div
              key={i}
              style={{
                ...styles.message,
