              }}
            >
              {em === 'neutral' && 'ðŸ˜'}
              {em === 'happy' && 'ðŸ˜Š'}
              {em === 'sad' && 'ðŸ˜¢'}
              {em === 'thinking' && 'ðŸ¤”'}
              {em === 'surprised' && 'ðŸ˜®'}
            </button>
          ))}
        </div>
      </div>

      {/* Reset button */}
      <button
        onClick={() => {
          setPhase('welcome');
          setMessages([]);
          setCloneData({ name: '', personality: [], speakingStyle: '', memories: [], faceData: null, faceImage: null, voiceProfile: null });
          setCurrentQuestion(0);
          setPersonalityQuestions(prev => prev.map(q => ({ ...q, a: '' })));
          setSampleConvo('');
          setCatchphrases('');
          setCapturedImage(null);
          setFaceFeatures({
            skinTone: '#f5c6a5',
            hairColor: '#1a1a1a',
            eyeColor: '#4a3728',
            faceShape: 'oval',
            faceWidth: 1.0,
            faceLength: 1.2,
            eyeSize: 0.2,
            eyeDistance: 0.35,
            noseSize: 0.15,
            mouthWidth: 0.3,
            browThickness: 0.08,
            hasGlasses: false,
            hairStyle: 'short',
          });
        }}
        style={styles.resetBtn}
      >
        ðŸ”„ Táº¡o Clone má»›i
      </button>

      {/* Small face reference in corner */}
      {capturedImage && (
        <div style={styles.faceReference}>
          <img src={capturedImage} alt="Your face" style={styles.faceRefImage} />
          <span>Báº¡n</span>
        </div>
      )}
    </div>
  );

  // Render based on phase
  return (
    <>
      {phase === 'welcome' && renderWelcome()}
      {phase === 'facescan' && renderFaceScan()}
      {phase === 'training' && renderTraining()}
      {phase === 'personality' && renderPersonality()}
