import React, { useState, useEffect } from 'react';
import Home from './Home';
import Chat from './Chat';
import HumanClone from './HumanClone';
import Admin from './Admin';
import Login from './Login';
import Register from './Register';
import UserHome from './UserHome';
import './App.css';

function App() {
  const [mode, setMode] = useState('home'); // 'home', 'chat', 'clone', 'admin', 'login', 'register'
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);

  // Check if user is already logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');
    const savedRole = localStorage.getItem('role');
    
    if (token && savedUser && savedRole) {
      setIsLoggedIn(true);
      setUser(JSON.parse(savedUser));
      setRole(savedRole);
    }
  }, []);

  const handleLogin = ({ user, role, token }) => {
    setIsLoggedIn(true);
    setUser(user);
    setRole(role);
    setMode('home');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('role');
    setIsLoggedIn(false);
    setUser(null);
    setRole(null);
    setMode('login');
  };

  // If not logged in, show login/register page
  if (!isLoggedIn) {
    if (mode === 'register') {
      return (
        <Register
          onRegisterSuccess={() => {
            setMode('login');
          }}
          onBackToLogin={() => {
            setMode('login');
          }}
        />
      );
    }
    return (
      <Login
        onLogin={handleLogin}
        onSwitchToRegister={() => setMode('register')}
      />
    );
  }

  // Role-based routing
  // Superadmin → Full Admin Dashboard
  // User → User Home (chat, personal clones)
  if (role === 'superadmin') {
    return (
      <>
        {mode === 'home' && <Home onSelectMode={setMode} user={user} role={role} onLogout={handleLogout} />}
        {mode === 'admin' && <Admin user={user} onLogout={handleLogout} />}
        {mode === 'chat' && (
          <div style={{ position: 'relative' }}>
            <button
              onClick={() => setMode('home')}
              style={{
                position: 'absolute',
                top: 20,
                right: 20,
                zIndex: 1000,
                background: 'linear-gradient(135deg, #ff9a56 0%, #ff7043 100%)',
                color: 'white',
                border: 'none',
                padding: '12px 24px',
                borderRadius: '10px',
                cursor: 'pointer',
                fontSize: '1rem',
                fontWeight: 'bold',
              }}
            >
              ← Quay lại
            </button>
            <Chat />
          </div>
        )}
        {mode === 'clone' && (
          <div style={{ position: 'relative' }}>
            <button
              onClick={() => setMode('home')}
              style={{
                position: 'absolute',
                top: 20,
                left: 20,
                zIndex: 1000,
                background: 'rgba(255,255,255,0.1)',
                color: 'white',
                border: '2px solid rgba(255,255,255,0.2)',
                padding: '10px 20px',
                borderRadius: '25px',
                cursor: 'pointer',
                fontSize: '14px',
              }}
            >
              ← Trang chủ
            </button>
            <HumanClone />
          </div>
        )}
      </>
    );
  }

  // User view
  return (
    <>
      {mode === 'home' && <UserHome user={user} role={role} onLogout={handleLogout} />}
      {mode === 'clone' && (
        <div style={{ position: 'relative' }}>
          <button
            onClick={() => setMode('home')}
            style={{
              position: 'absolute',
              top: 20,
              left: 20,
              zIndex: 1000,
              background: 'rgba(255,255,255,0.1)',
              color: 'white',
              border: '2px solid rgba(255,255,255,0.2)',
              padding: '10px 20px',
              borderRadius: '25px',
              cursor: 'pointer',
              fontSize: '14px',
            }}
          >
            ← Trang chủ
          </button>
          <HumanClone />
        </div>
      )}
    </>
  );
}

export default App;
