import React, { useState } from 'react';
import Home from './Home';
import Chat from './Chat';
import HumanClone from './HumanClone';
import Admin from './Admin';
import './App.css';

function App() {
  const [mode, setMode] = useState('home'); // 'home', 'chat', 'clone', 'admin'

  return (
    <>
      {mode === 'home' && <Home onSelectMode={setMode} />}
      {mode === 'admin' && <Admin />}
      {mode === 'chat' && (
        <div style={{ position: 'relative' }}>
          <button
            onClick={() => setMode('home')}
            style={{
              position: 'absolute',
              top: 20,
              right: 20,
              zIndex: 1000,
              background: 'linear-gradient(135deg, #ff9a56 0%, #ff7043 100%)',
              color: 'white',
              border: 'none',
              padding: '12px 24px',
              borderRadius: '10px',
              cursor: 'pointer',
              fontSize: '1rem',
              fontWeight: 'bold',
              boxShadow: '0 4px 15px rgba(255, 112, 67, 0.3)',
              transition: 'all 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'scale(1.05)';
              e.target.style.boxShadow = '0 8px 25px rgba(255, 112, 67, 0.5)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'scale(1)';
              e.target.style.boxShadow = '0 4px 15px rgba(255, 112, 67, 0.3)';
            }}
          >
            ← Quay lại
          </button>
          <Chat />
        </div>
      )}
      {mode === 'clone' && (
        <div style={{ position: 'relative' }}>
          <button
            onClick={() => setMode('home')}
            style={{
              position: 'absolute',
              top: 20,
              left: 20,
              zIndex: 1000,
              background: 'rgba(255,255,255,0.1)',
              color: 'white',
              border: '2px solid rgba(255,255,255,0.2)',
              padding: '10px 20px',
              borderRadius: '25px',
              cursor: 'pointer',
              fontSize: '14px',
              backdropFilter: 'blur(10px)',
              transition: 'all 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.target.style.background = 'rgba(255,255,255,0.2)';
            }}
            onMouseLeave={(e) => {
              e.target.style.background = 'rgba(255,255,255,0.1)';
            }}
          >
            ← Trang chủ
          </button>
          <HumanClone />
        </div>
      )}
    </>
  );
}

export default App;
