import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FiSend, FiTrash2, FiSettings, FiUpload, FiChevronUp } from 'react-icons/fi';
import './App.css';

const API_URL = 'http://localhost:8000';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [uploadQuery, setUploadQuery] = useState('');
  const [userName, setUserName] = useState('You');
  const messagesEndRef = useRef(null);
  const imageInputRef = useRef(null);
  const documentInputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    loadHistory();
  }, []);

  const loadHistory = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/history`);
      if (response.data.messages) {
        setMessages(response.data.messages);
      }
    } catch (error) {
      console.error('Failed to load history:', error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    setLoading(true);
    const userMessage = input;
    setInput('');

    setMessages(prev => [...prev, {
      user_message: userMessage,
      ai_response: '',
      timestamp: new Date().toISOString(),
      isLoading: true
    }]);

    try {
      const response = await axios.post(`${API_URL}/api/chat`, {
        text: userMessage,
        user_name: 'User'
      });

      setMessages(prev => {
        const updated = [...prev];
        updated[updated.length - 1] = {
          ...response.data,
          isLoading: false
        };
        return updated;
      });
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages(prev => {
        const updated = [...prev];
        updated[updated.length - 1] = {
          ...updated[updated.length - 1],
          ai_response: '‚ùå L·ªói k·∫øt n·ªëi. Vui l√≤ng ki·ªÉm tra server.',
          isLoading: false
        };
        return updated;
      });
    }

    setLoading(false);
  };

  const clearHistory = async () => {
    if (window.confirm('B·∫°n ch·∫Øc ch·∫Øn mu·ªën x√≥a l·ªãch s·ª≠ cu·ªôc tr√≤ chuy·ªán?')) {
      try {
        await axios.delete(`${API_URL}/api/history`);
        setMessages([]);
      } catch (error) {
        console.error('Failed to clear history:', error);
      }
    }
  };

  const uploadImage = async (file) => {
    if (!file) return;
    
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', uploadQuery);

    try {
      const response = await axios.post(`${API_URL}/api/upload/image`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      setMessages(prev => [...prev, {
        user_message: `[Image: ${file.name}]${uploadQuery ? ` - ${uploadQuery}` : ''}`,
        ai_response: response.data.ai_response,
        timestamp: response.data.timestamp
      }]);
      
      setUploadQuery('');
      if (imageInputRef.current) imageInputRef.current.value = '';
    } catch (error) {
      console.error('Image upload failed:', error);
      alert('Failed to upload image');
    } finally {
      setLoading(false);
    }
  };

  const uploadDocument = async (file) => {
    if (!file) return;
    
    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('query', uploadQuery);

    try {
      const response = await axios.post(`${API_URL}/api/upload/document`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      
      setMessages(prev => [...prev, {
        user_message: `[Document: ${file.name}]${uploadQuery ? ` - ${uploadQuery}` : ''}`,
        ai_response: response.data.ai_response || response.data.message,
        timestamp: response.data.timestamp || new Date().toISOString()
      }]);
      
      setUploadQuery('');
      if (documentInputRef.current) documentInputRef.current.value = '';
    } catch (error) {
      console.error('Document upload failed:', error);
      alert('Failed to upload document');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl bg-white rounded-3xl shadow-2xl overflow-hidden flex flex-col h-screen max-h-screen md:max-h-[700px] border border-gray-200">
        
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-600 to-blue-700 p-5 text-white flex justify-between items-center">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-white bg-opacity-30 rounded-full flex items-center justify-center text-xl">
              ü§ñ
            </div>
            <div>
              <h1 className="text-2xl font-bold">AIClone Chat</h1>
              <p className="text-xs opacity-90">Tr·∫£ l·ªùi t·ª´ AI</p>
            </div>
          </div>
          <button
            onClick={() => setShowSettings(!showSettings)}
            className="p-2 hover:bg-white hover:bg-opacity-20 rounded-lg transition"
            title="C√†i ƒë·∫∑t"
          >
            <FiSettings size={24} />
          </button>
        </div>

        {/* Settings Panel */}
        {showSettings && (
          <div className="bg-blue-50 border-b border-gray-300 p-4 flex gap-2 items-center">
            <input
              type="text"
              value={userName}
              onChange={(e) => setUserName(e.target.value || 'You')}
              placeholder="T√™n c·ªßa b·∫°n..."
              className="flex-1 border border-gray-300 rounded-lg px-3 py-2 text-sm focus:outline-none focus:border-blue-500"
              maxLength={20}
            />
            <button
              onClick={clearHistory}
              className="flex items-center gap-2 bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition text-sm font-medium"
              title="X√≥a l·ªãch s·ª≠"
            >
              <FiTrash2 size={16} /> X√≥a
            </button>
          </div>
        )}

        {/* Messages Container */}
        <div className="flex-1 overflow-y-auto p-5 space-y-4 bg-gray-50">
          {messages.length === 0 ? (
            <div className="flex items-center justify-center h-full text-center">
              <div>
                <p className="text-6xl mb-4">üëã</p>
                <p className="text-gray-600 text-lg font-medium">Xin ch√†o {userName}!</p>
                <p className="text-gray-500 text-sm mt-2">H√£y b·∫Øt ƒë·∫ßu cu·ªôc tr√≤ chuy·ªán v·ªõi t√¥i</p>
              </div>
            </div>
          ) : (
            messages.map((msg, idx) => (
              <div key={idx} className="space-y-3">
                {/* User Message */}
                <div className="flex justify-end gap-2">
                  <div className="max-w-xs">
                    <div className="text-xs text-gray-500 text-right mb-1 px-2">{userName}</div>
                    <div className="bg-blue-600 text-white rounded-3xl rounded-tr-none px-5 py-3 shadow-md break-words">
                      <p className="text-sm">{msg.user_message}</p>
                    </div>
                  </div>
                  <div className="w-8 h-8 rounded-full bg-blue-100 flex items-center justify-center flex-shrink-0 text-lg">
                    üë§
                  </div>
                </div>

                {/* AI Response */}
                <div className="flex justify-start gap-2">
                  <div className="w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center flex-shrink-0 text-white text-lg">
                    ü§ñ
                  </div>
                  <div className="max-w-xs">
                    <div className="text-xs text-gray-500 mb-1 px-2">AIClone</div>
                    <div className="bg-gray-200 text-gray-800 rounded-3xl rounded-tl-none px-5 py-3 shadow-md break-words">
                      {msg.isLoading ? (
                        <div className="flex gap-2 items-center">
                          <span>ƒêang suy nghƒ©</span>
                          <div className="flex gap-1">
                            <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" />
                            <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }} />
                            <div className="w-2 h-2 bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }} />
                          </div>
                        </div>
                      ) : (
                        <p className="text-sm">{msg.ai_response}</p>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Input Form */}
        <div className="bg-gradient-to-r from-slate-50 to-blue-50 border-t-2 border-blue-200 p-4">
          <form onSubmit={sendMessage} className="flex gap-2 items-center">
            {/* Image Upload Button */}
            <button
              type="button"
              onClick={() => imageInputRef.current?.click()}
              disabled={loading}
              className="bg-gradient-to-br from-emerald-400 to-emerald-600 hover:from-emerald-500 hover:to-emerald-700 disabled:from-gray-300 disabled:to-gray-400 text-white rounded-full p-3 transition-all duration-200 shadow-md hover:shadow-lg hover:scale-105 active:scale-95 flex items-center justify-center"
              title="üì∑ T·∫£i ·∫£nh"
            >
              <span className="text-xl">üì∑</span>
            </button>
            <input
              ref={imageInputRef}
              type="file"
              accept="image/*"
              onChange={(e) => uploadImage(e.target.files?.[0])}
              style={{ display: 'none' }}
            />
            
            {/* Document Upload Button */}
            <button
              type="button"
              onClick={() => documentInputRef.current?.click()}
              disabled={loading}
              className="bg-gradient-to-br from-blue-400 to-blue-600 hover:from-blue-500 hover:to-blue-700 disabled:from-gray-300 disabled:to-gray-400 text-white rounded-full p-3 transition-all duration-200 shadow-md hover:shadow-lg hover:scale-105 active:scale-95 flex items-center justify-center"
              title="üìÑ T·∫£i file"
            >
              <span className="text-xl">üìÑ</span>
            </button>
            <input
              ref={documentInputRef}
              type="file"
              accept=".pdf,.docx,.txt"
              onChange={(e) => uploadDocument(e.target.files?.[0])}
              style={{ display: 'none' }}
            />

            {/* Message Input */}
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Nh·∫≠p tin nh·∫Øn..."
              className="flex-1 border-2 border-gray-300 rounded-full px-5 py-3 text-base text-gray-800 font-medium focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 focus:shadow-lg bg-white placeholder-gray-400 transition-all duration-200"
              disabled={loading}
            />
            
            {/* Send Button */}
            <button
              type="submit"
              disabled={loading || !input.trim()}
              className="bg-gradient-to-br from-blue-500 to-blue-700 hover:from-blue-600 hover:to-blue-800 disabled:from-gray-300 disabled:to-gray-400 text-white rounded-full p-3 transition-all duration-200 shadow-md hover:shadow-lg hover:scale-105 active:scale-95 flex items-center justify-center"
              title="G·ª≠i"
            >
              <FiSend size={22} />
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Chat;
