import React, { useState, useRef, useCallback, useEffect } from 'react';
import axios from 'axios';

const HumanClone = () => {
  // Core states
  const [phase, setPhase] = useState('welcome'); // welcome, facescan, analyzing, training, personality, clone
  const [cloneData, setCloneData] = useState({
    name: '',
    personality: [],
    speakingStyle: '',
    memories: [],
    faceData: null,
    faceImage: null,
    voiceProfile: null,
  });

  // Face scan states
  const [cameraActive, setCameraActive] = useState(false);
  const [capturedImage, setCapturedImage] = useState(null);
  const [scanStatus, setScanStatus] = useState('');
  const [isScanning, setIsScanning] = useState(false);
  const [scanProgress, setScanProgress] = useState(0);
  const webcamRef = useRef(null);
  const photoCanvasRef = useRef(null);
  
  // Face analysis data - extracted from photo
  const [faceFeatures, setFaceFeatures] = useState({
    skinTone: '#f5c6a5',      // MÃ u da
    hairColor: '#1a1a1a',     // MÃ u tÃ³c
    eyeColor: '#4a3728',      // MÃ u máº¯t
    faceShape: 'oval',        // HÃ¬nh dÃ¡ng máº·t: oval, round, square, heart
    faceWidth: 1.0,           // Äá»™ rá»™ng máº·t
    faceLength: 1.2,          // Äá»™ dÃ i máº·t
    eyeSize: 0.2,             // KÃ­ch thÆ°á»›c máº¯t
    eyeDistance: 0.35,        // Khoáº£ng cÃ¡ch giá»¯a 2 máº¯t
    noseSize: 0.15,           // KÃ­ch thÆ°á»›c mÅ©i
    mouthWidth: 0.3,          // Äá»™ rá»™ng miá»‡ng
    browThickness: 0.08,      // Äá»™ dÃ y lÃ´ng mÃ y
    hasGlasses: false,        // CÃ³ Ä‘eo kÃ­nh khÃ´ng
    hairStyle: 'short',       // Kiá»ƒu tÃ³c: short, long, bald
  });

  // Training data
  const [trainingMessages, setTrainingMessages] = useState([]);
  const [personalityQuestions, setPersonalityQuestions] = useState([
    { q: 'Báº¡n mÃ´ táº£ tÃ­nh cÃ¡ch cá»§a mÃ¬nh nhÆ° tháº¿ nÃ o?', a: '', category: 'personality' },
    { q: 'Khi vui báº¡n thÆ°á»ng nÃ³i gÃ¬?', a: '', category: 'emotion' },
    { q: 'Khi buá»“n báº¡n thÆ°á»ng lÃ m gÃ¬?', a: '', category: 'emotion' },
    { q: 'CÃ¢u nÃ³i cá»­a miá»‡ng cá»§a báº¡n lÃ  gÃ¬?', a: '', category: 'speech' },
    { q: 'Báº¡n thÃ­ch nÃ³i chuyá»‡n kiá»ƒu nÃ o? (hÃ i hÆ°á»›c, nghiÃªm tÃºc, thÃ¢n thiá»‡n...)', a: '', category: 'style' },
    { q: 'Sá»Ÿ thÃ­ch cá»§a báº¡n lÃ  gÃ¬?', a: '', category: 'interest' },
    { q: 'Báº¡n ghÃ©t Ä‘iá»u gÃ¬ nháº¥t?', a: '', category: 'dislike' },
    { q: 'Má»¥c tiÃªu trong cuá»™c sá»‘ng cá»§a báº¡n?', a: '', category: 'goal' },
  ]);
  const [currentQuestion, setCurrentQuestion] = useState(0);

  // Clone interaction
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isThinking, setIsThinking] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [cloneEmotion, setCloneEmotion] = useState('neutral');
  const [cloneMood, setCloneMood] = useState(70); // 0-100 mood meter

  // Refs
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  
  // Input refs to maintain focus
  const nameInputRef = useRef(null);
  const answerInputRef = useRef(null);

  // Handle name change
  const handleNameChange = (e) => {
    setCloneData(prev => ({ ...prev, name: e.target.value }));
  };

  // Handle answer change in training
  const handleAnswer = (answer) => {
    const updated = [...personalityQuestions];
    updated[currentQuestion].a = answer;
    setPersonalityQuestions(updated);
  };

  const nextQuestion = () => {
    if (currentQuestion < personalityQuestions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      // Compile personality
      const personality = personalityQuestions.map(q => ({
        category: q.category,
        response: q.a,
      }));
      setCloneData(prev => ({ ...prev, personality }));
      setPhase('personality');
    }
  };

  // Speaking style states (moved to parent)
  const [sampleConvo, setSampleConvo] = useState('');
  const [catchphrases, setCatchphrases] = useState('');

  const finishSetup = async () => {
    try {
      await axios.post('http://localhost:8000/api/clone/setup', {
        name: cloneData.name,
        personality: cloneData.personality,
        speakingStyle: sampleConvo,
        catchphrases: catchphrases.split('\n').filter(p => p.trim()),
        faceImage: capturedImage,
      });
    } catch (e) {
      console.log('Backend not available, using local mode');
    }

    const newCloneData = {
      ...cloneData,
      speakingStyle: sampleConvo,
      faceImage: capturedImage,
      memories: [],
      createdAt: new Date().toLocaleString('vi-VN'),
    };

    setCloneData(newCloneData);
    
    // Save clone to localStorage
    try {
      const existingClones = JSON.parse(localStorage.getItem('AIClones') || '[]');
      existingClones.push(newCloneData);
      localStorage.setItem('AIClones', JSON.stringify(existingClones));
    } catch (e) {
      console.error('Error saving to localStorage:', e);
    }
    
    setPhase('clone');
  };

  // ============ CAMERA FUNCTIONS ============
  const startCamera = async () => {
    try {
      setScanStatus('Äang má»Ÿ camera...');
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user'
        } 
      });
      if (webcamRef.current) {
        webcamRef.current.srcObject = stream;
        webcamRef.current.play();
        setCameraActive(true);
        setScanStatus('HÃ£y Ä‘Æ°a khuÃ´n máº·t vÃ o khung hÃ¬nh');
      }
    } catch (err) {
      console.error('Camera error:', err);
      setScanStatus('KhÃ´ng thá»ƒ má»Ÿ camera. Vui lÃ²ng cho phÃ©p truy cáº­p camera.');
    }
  };

  const stopCamera = () => {
    if (webcamRef.current && webcamRef.current.srcObject) {
      const tracks = webcamRef.current.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      webcamRef.current.srcObject = null;
    }
    setCameraActive(false);
  };

  const capturePhoto = () => {
    if (!webcamRef.current || !photoCanvasRef.current) return;
    
    const video = webcamRef.current;
    const canvas = photoCanvasRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    
    const ctx = canvas.getContext('2d');
    // Mirror the image
    ctx.translate(canvas.width, 0);
    ctx.scale(-1, 1);
    ctx.drawImage(video, 0, 0);
    
    const imageData = canvas.toDataURL('image/png');
    setCapturedImage(imageData);
    setCloneData(prev => ({ ...prev, faceImage: imageData }));
    setScanStatus('ðŸ“¸ ÄÃ£ chá»¥p! Äang phÃ¢n tÃ­ch khuÃ´n máº·t...');
    stopCamera();
    
    // Start face analysis
    analyzeFace(canvas);
  };

  // ============ FACE ANALYSIS ============
  const analyzeFace = async (canvas) => {
