// Add keyframe animations via style tag (only once)
if (!document.getElementById('humanclone-animations')) {
  const styleSheet = document.createElement('style');
  styleSheet.id = 'humanclone-animations';
  styleSheet.textContent = `
    @keyframes pulse {
      0%, 100% { transform: scale(1); opacity: 1; }
      50% { transform: scale(1.05); opacity: 0.8; }
    }
    @keyframes talking {
      0%, 100% { transform: translate(-50%, -50%) scale(1); }
      50% { transform: translate(-50%, -50%) scale(1.02); }
    }
    @keyframes thinking {
      0%, 100% { transform: rotate(-1deg) scale(0.98); }
      50% { transform: rotate(1deg) scale(1); }
    }
    @keyframes breathing {
      0%, 100% { transform: translate(-50%, -50%) scale(1); }
      50% { transform: translate(-50%, -50%) scale(1.02); }
    }
    @keyframes scanMove {
      0% { top: 10%; }
      50% { top: 90%; }
      100% { top: 10%; }
    }
    @keyframes scanLine {
      0% { top: 10%; }
      100% { top: 90%; }
    }
    @keyframes speaking {
      0%, 100% { transform: scale(1); }
      25% { transform: scale(1.01) translateY(-2px); }
      50% { transform: scale(1.03); }
      75% { transform: scale(1.01) translateY(2px); }
    }
    @keyframes idle {
      0%, 100% { transform: scale(1); }
      50% { transform: scale(1.01); }
    }
    @keyframes waveAnim {
      0%, 100% { height: 8px; }
      50% { height: 32px; }
    }
    @keyframes glowPulse {
      0%, 100% { opacity: 0.8; }
      50% { opacity: 1; }
    }
  `;
  document.head.appendChild(styleSheet);
}

export default HumanClone;
