    if (emotion === 'happy') setCloneMood(prev => Math.min(100, prev + 5));
    if (emotion === 'sad') setCloneMood(prev => Math.max(0, prev - 5));

    setMessages(prev => [...prev, { role: 'clone', content: cloneResponse }]);

    // Speak the response
    speakResponse(cloneResponse);
  };

  // Text-to-speech
  const speakResponse = (text) => {
    setIsSpeaking(true);

    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = 'vi-VN';
    utterance.rate = 1.0;
    utterance.pitch = 1.0;

    utterance.onend = () => {
      setIsSpeaking(false);
      setCloneEmotion('neutral');
    };

    window.speechSynthesis.speak(utterance);
  };

  // Voice input
  const startListening = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = 'vi-VN';
    recognition.continuous = false;

    recognition.onstart = () => setIsListening(true);
    recognition.onresult = (e) => {
      const text = e.results[0][0].transcript;
      setInputText(text);
      setIsListening(false);
    };
    recognition.onerror = () => setIsListening(false);
    recognition.start();
  };

  // Clone Screen
  const CloneScreen = () => (
    <div style={{ width: '100%', height: '100vh', position: 'relative' }}>
      <div ref={mountRef} style={{ width: '100%', height: '100%' }} />

      {/* Clone Info */}
      <div style={styles.cloneInfo}>
