import React, { useState } from 'react';
import axios from 'axios';
import './AuthContainer.css';

const Login = ({ onLogin, onSwitchToRegister, onSwitchToForgotPassword }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await axios.post('http://localhost:8000/api/auth/login', {
        username,
        password
      });

      const { access_token, user, role } = response.data;
      
      // Save token & user info
      localStorage.setItem('token', access_token);
      localStorage.setItem('user', JSON.stringify(user));
      localStorage.setItem('role', role);

      onLogin({ user, role, token: access_token });
    } catch (e) {
      setError(e.response?.data?.detail || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="form-wrapper">
        <h1 className="form-title">üîê AIClone</h1>
        <p className="form-subtitle">Welcome back! Log in to your account</p>

        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label className="form-label">Username</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              className="form-input"
              disabled={loading}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <div className="input-wrapper">
              <input
                type={showPassword ? "text" : "password"}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                className="form-input"
                disabled={loading}
                required
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="toggle-password"
              >
                {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
              </button>
            </div>
          </div>

          {error && <div className="error-message">{error}</div>}

          <button
            type="submit"
            className={`form-button ${loading ? 'loading' : ''}`}
            disabled={loading}
          >
            {loading ? '‚è≥ Logging in...' : 'üöÄ Login'}
          </button>
        </form>

        <div className="form-divider">or</div>

        <button
          onClick={onSwitchToForgotPassword}
          className="form-button-secondary"
          disabled={loading}
        >
          üîë Forgot Password?
        </button>

        <div className="form-divider" style={{ margin: '25px 0 20px 0' }}>New here?</div>

        <button
          onClick={onSwitchToRegister}
          className="form-button-secondary"
          disabled={loading}
        >
          ‚ú® Create Account
        </button>

        <div className="form-footer">
          <p style={{ color: 'rgba(255,255,255,0.6)', fontSize: '0.85rem' }}>
            By logging in, you agree to our Terms & Conditions
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
