import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Admin = () => {
  const [clones, setClones] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedClone, setSelectedClone] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterEmotion, setFilterEmotion] = useState('all');
  const [sortBy, setSortBy] = useState('name');

  // Load clones from localStorage
  useEffect(() => {
    try {
      const storedClones = localStorage.getItem('AIClones');
      if (storedClones) {
        setClones(JSON.parse(storedClones));
      }
      setLoading(false);
    } catch (error) {
      console.error('Error loading clones:', error);
      setLoading(false);
    }
  }, []);

  // Save clones to localStorage
  const saveClones = (updatedClones) => {
    localStorage.setItem('AIClones', JSON.stringify(updatedClones));
    setClones(updatedClones);
  };

  // Delete clone
  const deleteClone = (id) => {
    if (window.confirm('B·∫°n ch·∫Øc ch·∫Øn mu·ªën x√≥a clone n√†y?')) {
      const updated = clones.filter((c, idx) => idx !== id);
      saveClones(updated);
      setSelectedClone(null);
      setEditMode(false);
    }
  };

  // Update clone
  const updateClone = (id, updatedData) => {
    const updated = clones.map((c, idx) => (idx === id ? { ...c, ...updatedData } : c));
    saveClones(updated);
    setSelectedClone(updatedData);
  };

  // Filter & sort clones
  const filtered = clones
    .filter(c => c.name.toLowerCase().includes(searchTerm.toLowerCase()))
    .sort((a, b) => {
      if (sortBy === 'name') return a.name.localeCompare(b.name);
      if (sortBy === 'date') return new Date(b.createdAt) - new Date(a.createdAt);
      return 0;
    });

  if (loading) {
    return <div style={styles.fullScreen}><p>Loading...</p></div>;
  }

  return (
    <div style={styles.adminContainer}>
      {/* Header */}
      <div style={styles.header}>
        <h1 style={styles.title}>üß¨ Admin Dashboard - Qu·∫£n l√Ω AI Clones</h1>
        <button
          onClick={() => window.location.href = '/'}
          style={styles.backBtn}
        >
          ‚Üê Quay l·∫°i Home
        </button>
      </div>

      <div style={styles.content}>
        {/* Left Panel - List */}
        <div style={styles.leftPanel}>
          <div style={styles.searchBox}>
            <input
              type="text"
              placeholder="üîç T√¨m clone..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={styles.searchInput}
            />
          </div>

          <div style={styles.controls}>
            <div style={styles.controlGroup}>
              <label style={styles.label}>S·∫Øp x·∫øp:</label>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                style={styles.select}
              >
                <option value="name">Theo t√™n</option>
                <option value="date">Theo ng√†y t·∫°o</option>
              </select>
            </div>
          </div>

          <div style={styles.clonesList}>
            <h3 style={styles.listTitle}>Danh s√°ch ({filtered.length})</h3>
            {filtered.length === 0 ? (
              <p style={styles.emptyText}>Ch∆∞a c√≥ clone n√†o</p>
            ) : (
              filtered.map((clone, idx) => (
                <div
                  key={idx}
                  onClick={() => {
                    setSelectedClone({ ...clone, id: idx });
                    setEditMode(false);
                  }}
                  style={{
                    ...styles.cloneCard,
                    background: selectedClone?.id === idx ? '#ff7043' : 'rgba(255,255,255,0.05)',
                    borderColor: selectedClone?.id === idx ? '#ff7043' : 'rgba(255,255,255,0.1)',
